#
# Function: Initialize
#
# Description: Initialize all global variables.
#
sub Initialize()
{
	my @s;

	$InstallDir = `dds_cfgutil installdir`;
	chomp( $InstallDir );
	$InstallDir =~ s/\\/\//g;
	$InstallDir =~ s/\/bin//g;

	$HostName = `dds_hostnm`;
	chomp( $HostName );
	@s = split (/\s/,$HostName);
	$HostName=$s[0];
	$ManagerHost = $HostName;

	$BaseURL = GetBaseURL();

	$DTMLogDir = `dds_cfgutil logdir`;
	chomp( $DTMLogDir );
	$DTMLogDir =~ s/\\/\//g;

	$AppReportsDir = $DTMLogDir . "/job_reports";

	$JobID = q|%job_id%|;
	$JobName = q|%job_name%|;
	$JobGroupId = q|%job_group_id%|;
	$JobRunNumber = q|%job_run_number%|;

	my $RunNumberFormatted = sprintf("%04d", $JobRunNumber);

	$ReportPath = $AppReportsDir . $SLASH . $JobName;
	$ReportFile = $ReportPath . $SLASH . $JobName . "_Run#" . $RunNumberFormatted . ".html";

	$MaxTableRows=500;

	#$ProductName = `dds_cfgutil product_name`;
	$ProductName = "Signiant";
	chomp( $ProductName );

	$TableRowCount = 0;
	$HtmlReportString="";

	if ($ReportingTimezone eq "" || $ReportingTimezone =~ /^\%/) {
		$ReportingTimezone = "GMT";
	}

	if ($EmailSubject eq "")
	{
		$EmailSubject = $JobName;
	}

	$WorkflowContainsSigniantTransfers = FALSE;
	$FilesTransferred = FALSE;
	$FilesChanged = FALSE;
	$WorkUnitsCompleted = FALSE;
	$ErrorsOccurred = FALSE;
	$jobSucceeded = TRUE;
	$SendEmail = FALSE;

	$REPORT_RC = 0;
}

sub LogVars
{
	SigPrintLog( "Email To: [$EmailTo]",LOGINFO);
	SigPrintLog( "Email Cc: [$EmailCc]",LOGINFO);
	SigPrintLog( "Email Bcc: [$EmailBcc]",LOGINFO);
	SigPrintLog( "Email Job Failure Report To: [$EmailJobFailureReportTo]",LOGINFO);
	SigPrintLog( "Email Subject: [$EmailSubject]",LOGINFO);
	SigPrintLog( "Email Condition: [$EmailCondition]",LOGINFO);
	SigPrintLog( "Report Versions To Maintain: [$ReportVersions]",LOGINFO);
	SigPrintLog( "Reporting Timezone: [$ReportingTimezone]",LOGINFO);
	SigPrintLog( "SNMP Trap Receivers: [$SNMPTrapReceivers]",LOGINFO);
	SigPrintLog( "SNMP Trap Types: [$SNMPTrapTypes]",LOGINFO);
}

sub ValidateInputs()
{
	if (($EmailTo eq "") && ($EmailCc eq "") && ($EmailBcc eq ""))
	{
		SigPrintLog("WARNING: No email recipient(s) specified for Job Report",LOGWARN);
	}

	if ($SNMPTrapTypes ne "")
	{
		if (lc($SNMPTrapTypes) != m/success|failure/)
		{
			SigPrintLog("ERROR: Invalid SNMP Trap Type specified: [$SNMPTrapTypes]",LOGERROR);
			$RC=1;
		}

		if ($SNMPTrapReceivers eq "")
		{
			SigPrintLog("ERROR: No SNMP Trap Receivers specified",LOGERROR);
			$RC=1;
		}

		if ($SNMPTrapCommunityString eq "")
		{
			SigPrintLog("WARNING: No SNMP Trap Community String specified - assuming \"public\"",LOGWARN);
			$SNMPTrapCommunityString = "public";
		}

		my @ReceiverArray = split(/,/,$SNMPTrapReceivers);
		my @ValidReceiverArray;

		foreach my $receiver (@ReceiverArray)
		{

			if (!IsValidIPAddress($receiver) && !IsValidFQDN($receiver))
			{
				SigPrintLog("ERROR: Invalid Hostname/IP Address for SNMP Trap Receiver: [$receiver]",LOGERROR);
				$RC=1;
			}
			else
			{
				push(@ValidReceiverArray,$receiver);
			}
		}

		$SNMPValidTrapReceivers = join(',',@ValidReceiverArray);
	}
}


#
# Get the base url for the web app...
#
sub GetBaseURL()  
{  
	my $APPCFGFILE;  
	my $ConfLine;
	my $FieldCount;
	my @s;
	my $URLFound;

	$APPCFGFILE = `dds_cfgutil appconfig_file`;  
	chomp( $APPCFGFILE );  
	$APPCFGFILE =~ s/\\/\//g;  

	$BaseURL = "$ManagerHost/signiant/"; # Default

	if (open(APPCFGFILE ,$APPCFGFILE))
	{
		while (!eof(APPCFGFILE ) && !$URLFound)
		{
			$ConfLine = <APPCFGFILE>;

			$ConfLine =~ s/[\n\r]//g;	# Remove newlines and/or line-feeds

			@s = split (/\s*=\s*/,$ConfLine); # Split based on = allowing for whitespace

			$FieldCount = scalar @s;

			if (uc($s[0]) eq "APPROOTURL")
			{
				$BaseURL = "$ManagerHost/$s[1]";
				$URLFound=TRUE;
			}
		}

		close(APPCFGFILE);
	}

	return $BaseURL;
}  

sub CreateReportFile()
{
	if (! DirExists($ReportPath) )
	{
		mkpath($ReportPath);
	}

	if ( open(REPORTFILE, "> $ReportFile") )
	{
		print REPORTFILE "$HtmlReportString";
		close(REPORTFILE);
	}
}

#
# PrintHtml:
#
sub PrintHtml($) 
{
	my $msg = $_[0];
	chomp($msg);

	$HtmlReportString = $HtmlReportString . "\n" . $msg;
}

sub PrintTableRow($$$) 
{
	my $target = $_[0];
	my $Label = $_[1];
	my $Value = $_[2];

	my $ValueTextFormat;
	my $LabelTextFormat="smallLabelText";

	if ($TableRowCount % 2 == 0)
	{
		$ValueTextFormat="smallValueText";
	}
	else
	{
		$ValueTextFormat="smallValueText2";
	}

	PrintHtml("<TR><TD CLASS=\"$LabelTextFormat\" WIDTH=\"30%\">$Label:</TD><TD CLASS=\"$ValueTextFormat\" WIDTH=\"70%\">$Value</TD></TR>");

	$TableRowCount++
}

sub CommaFormatted($)
{
	my $delimiter;
	my($n,$d);
	my @a;

	$delimiter = ','; # replace comma if desired
	($n,$d) = split /\./,shift,2;
	@a = ();

	while($n =~ /\d\d\d\d/)
	{
		$n =~ s/(\d\d\d)$//;
		unshift @a,$1;
	}

	unshift @a,$n;
	$n = join $delimiter,@a;
	$n = "$n\.$d" if $d =~ /\d/;
	return "$n";
}

sub TimeFormatted($)
{
	my $Time = $_[0];

	my $Hours;
	my $Minutes;
	my $Seconds;

	$Hours = int( $Time / 3600 );
	$Time = $Time % 3600;

	$Minutes = int( $Time / 60 );
	$Time = $Time % 60;

	$Seconds = $Time;

	my $TimeStr = "";

	if ($Hours == 1)
	{
		$TimeStr = "$Hours hr, ";
	}
	else
	{
		$TimeStr = "$Hours hrs, ";
	}

	if ($Minutes == 1)
	{
		$TimeStr = $TimeStr . "$Minutes min, ";
	}
	else
	{
		$TimeStr = $TimeStr . "$Minutes mins, ";
	}

	if ($Seconds == 1)
	{
		$TimeStr = $TimeStr . "$Seconds sec";
	}
	else
	{
		$TimeStr = $TimeStr . "$Seconds secs";
	}

	return "$TimeStr";
}

sub GetStats()  
{  
	$STATS_CONST_STR     = q|%workflow_const_stats%|;
	$STATS_SUMMARY_STR   = q|%workflow_summary_stats%|;
	$STATS_REPORT_STR    = q|%workflow_report_stats%|;
	$STATS_AGGREGATE_STR = q|%workflow_aggregate_stats%|;

	%STATS_CONST_HASH     = ParseStatsConstantsForJob($STATS_CONST_STR);
	%STATS_SUMMARY_HASH   = ParseStatsSummaryForJob($STATS_SUMMARY_STR);
	%STATS_REPORT_HASH    = ParseStatsReportForJob($STATS_REPORT_STR);
	%STATS_AGGREGATE_HASH = ParseStatsAggregateForJob($STATS_AGGREGATE_STR);

	my @KEYS = keys %STATS_AGGREGATE_HASH;

	foreach my $KEY ( @KEYS )
	{
		my %STATSHASH = %{$STATS_AGGREGATE_HASH{$KEY}};

		if (defined(%{$STATSHASH{constants}}))
		{
			my %CONSTS = %{$STATSHASH{constants}};

			my $ComponentType = $CONSTS{pkgtype};

			if (($ComponentType eq "FILE_TRF")   || 
				($ComponentType eq "OBJECT_TRF") || 
				($ComponentType eq "OBJECT_PUT") || 
				($ComponentType eq "OBJECT_GET"))
			{
				$WorkflowContainsSigniantTransfers = TRUE;
			}
		}
		else
		{
			SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
			SigPrintLog("Statistics hash 'constants' does not exist for key: $KEY",LOGDEBUG);
		}
	}

	if ($STATS_SUMMARY_STR eq "") # If there are no sumamry stats, things have gone badly...
	{
		SigPrintLog("ERROR: No summary statistics exist for the job - assuming Job Failure",LOGERROR);
		$RC=1;
	}
}

sub CheckFilesTransferredAndErrorsOccurred($)
{
	my $PassedRC=$_[0];

	if ($STATS_SUMMARY_STR eq "") # If there are no stats, things have gone badly...
	{
		$ErrorsOccurred = TRUE;
		$jobSucceeded = FALSE;
	}
	else
	{
		#
		# Check stats first...
		#
		my $totalFilesTransferred = GetStatRecord(\%STATS_SUMMARY_HASH,1,"ft","");

		if ($totalFilesTransferred > 0)
		{
			$FilesTransferred = TRUE;
		}

		my $totalFilesDeleted = GetStatRecord(\%STATS_SUMMARY_HASH,1,"fd","");
							  + GetStatRecord(\%STATS_SUMMARY_HASH,1,"dd","");

		if ($totalFilesTransferred > 0 or ($ConsiderDeletions and ($totalFilesDeleted > 0)))
		{
			$FilesChanged = TRUE;
		}

		my $totalWorkUnitsComplete = GetStatRecord(\%STATS_SUMMARY_HASH,1,"wuc","");

		if ($totalWorkUnitsComplete > 0)
		{
			$WorkUnitsCompleted = TRUE;
		}

		my $totalUnrecoveredErrors = GetStatRecord(\%STATS_SUMMARY_HASH,1,"pe","");

		if ($totalUnrecoveredErrors > 0 || $PassedRC ne 0)
		{
			$ErrorsOccurred = TRUE;
			$jobSucceeded = FALSE;
		}
	}

	#
	# Now check job log, but only if we haven't found any errors yet...
	# 
	if (!$ErrorsOccurred)
	{
		sleep(1);

		my $JOBLOG = q|%job_log_path%|;  
		chomp( $JOBLOG ); 

		if ($^O eq "MSWin32")
		{
			$JOBLOG =~ s/\//\\/g;  
		}
		else
		{
			$JOBLOG =~ s/\\/\//g;  
		}

		my $LogLine; 

		if (open(JOBLOG,$JOBLOG))
		{
			while (!eof(JOBLOG) && !$ErrorsOccurred) 
			{
				$LogLine= <JOBLOG>;

				$LogLine=~ s/[\n\r]//g;

				(my $datetime, my $severity, my $category, my $msgId, my $sourceAgent, my $jobTemplate, my $msgSource, my $message) = split (/[\t]/,$LogLine);

				if ($severity eq "ERROR" || uc($message) =~ m/^ERROR/)
				{	
					$ErrorsOccurred = TRUE;
				}
			}

			close(JOBLOG);
		}
	}
}

