sub PrintJobLog($$$)
{
	my $JobLogType = @_[0];
	my $JobLogAgent = @_[1];
	my $HeaderMsg = @_[2];

	my $JOBLOG;  
	my $LogLine;
	my $FieldCount;
	my @s;
	my $ValueTextFormat;
	my $Result;

	my $datetime;
	my $severity;
	my $category;
	my $msgId;
	my $sourceAgent;
	my $jobTemplate;
	my $msgSource;
	my $message;

	my $IsInfo;
	my $IsWarning;
	my $IsError;
	my $HeaderWritten;

	$JOBLOG = q|%job_log_path%|;  
	chomp( $JOBLOG );  

	if ($^O eq "MSWin32")
	{
		$JOBLOG =~ s/\//\\/g;  
	}
	else
	{
		$JOBLOG =~ s/\\/\//g;  
	}

	$HeaderWritten = 0;

	$TableRowCount=0;
	$MaxTableRows=500;

	if (open(JOBLOG,$JOBLOG))
	{
		while (!eof(JOBLOG) && ($TableRowCount < $MaxTableRows))
		{
			$LogLine= <JOBLOG>;

			$LogLine=~ s/[\n\r]//g;

			($datetime, $severity, $category, $msgId, $sourceAgent, $jobTemplate, $msgSource, $message) = split (/[\t]/,$LogLine);

			if ($severity eq "ERROR" || uc($message) =~ m/^ERROR/)
			{	
				$IsInfo     = FALSE;
				$IsWarning  = FALSE;
				$IsError    = TRUE;
			}
			elsif ($severity eq "WARN" || uc($message) =~ m/^WARN/)
			{	
				if (uc($message) =~ m/^TRANSFERRED/)
				{
					# Ignore the "Transferred..." debug messsages that have to be sent to STDERR and are automatically flagged as WARNs....
					$IsInfo     = TRUE;
					$IsWarning  = FALSE;
					$IsError    = FALSE;
				}
				else
				{
					$IsInfo     = FALSE;
					$IsWarning  = TRUE;
					$IsError    = FALSE;
				}
			}
			else
			{
				$IsInfo     = TRUE;
				$IsWarning  = FALSE;
				$IsError    = FALSE;
			}

			if ($TableRowCount % 2 == 0)
			{
				if ($IsError)
				{
					$ValueTextFormat="verySmallErrorText";
				}
				elsif ($IsWarning)
				{
					$ValueTextFormat="verySmallWarningText";
				}
				else
				{
					$ValueTextFormat="verySmallValueText";
				}
			}
			else
			{
				if ($IsError)
				{
					$ValueTextFormat="verySmallErrorText2";
				}
				elsif ($IsWarning)
				{
					$ValueTextFormat="verySmallWarningText2";
				}
				else
				{
					$ValueTextFormat="verySmallValueText2";
				}
			}

			if (lc($JobLogType) eq "full")
			{
				if (!$HeaderWritten)
				{
					PrintHtml("<HR>");
					PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
					PrintHtml("<TR><TD CLASS=\"bodyText\">");

					PrintHtml("<a name=\"joblog\"><b>Job Log</b></a>");

					PrintHtml("</TD></TR>");
					PrintHtml("</TABLE>");
					PrintHtml("<BR>");

					PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
					PrintHtml("<TR><TD CLASS=\"smallLabelText\">Job Log for Job: %job_name%</TD></TR>");
					PrintHtml("<TR><TD CLASS=\"smallLabelText\">Job Run Number: $JobRunNumber</TD></TR>");
					PrintHtml("</TABLE>");

					PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");

					PrintHtml("<TR>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"12%\">Date/Timestamp</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"12%\">Component</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"5%\">Severity</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"8%\">Message ID</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"15%\">Agent</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"48%\">Message</TD>");
					PrintHtml("</TR>");

					$HeaderWritten = 1;
				}

				PrintHtml("<TR>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"12%\">$datetime</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"12%\">$jobTemplate</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"5%\">$severity</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"8%\">$msgId</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"15%\">$sourceAgent</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"48%\">$message</TD>");
				PrintHtml("</TR>");

				$TableRowCount++;
			}
			elsif (((lc($JobLogType) eq "error")   && $IsError   && ($JobLogAgent eq $sourceAgent || $JobLogAgent eq "" )) ||
			       ((lc($JobLogType) eq "warning") && $IsWarning && ($JobLogAgent eq $sourceAgent || $JobLogAgent eq "" )) ||
			       ((lc($JobLogType) eq "error_warning") && ($IsError || $IsWarning) && ($JobLogAgent eq $sourceAgent || $JobLogAgent eq "" )))
			{
				if (!$HeaderWritten)
				{
					PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
					PrintHtml("<TR><TD CLASS=\"bodyText\">");

					PrintHtml("$HeaderMsg: $JobLogAgent");

					PrintHtml("</TD></TR>");
					PrintHtml("</TABLE>");
					PrintHtml("<BR>");

					PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");

					PrintHtml("<TR>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"12%\">Date/Timestamp</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"12%\">Component</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"5%\">Severity</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"8%\">Message ID</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"15%\">Agent</TD>");
					PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"48%\">Message</TD>");
					PrintHtml("</TR>");

					$HeaderWritten = 1;
				}

				PrintHtml("<TR>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"12%\">$datetime</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"12%\">$jobTemplate</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"5%\">$severity</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"8%\">$msgId</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"15%\">$sourceAgent</TD>");
				PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"48%\">$message</TD>");
				PrintHtml("</TR>");

				$TableRowCount++;
			}
		}

		close(JOBLOG);
	}
	else
	{
		PrintHtml("<b>WARNING: Job Log Not Found: $JOBLOG</b>");
	}

	if ($HeaderWritten)
	{
		PrintHtml("</TABLE>");

		PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
		PrintHtml("<TR>");

		if ($TableRowCount >= $MaxTableRows)
		{
			PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\">[Log truncated at $MaxTableRows lines]</TD>");
		}

		PrintHtml("</TR>");

		#
		# URL encode any variables that might have unusual chars (including spaces)...
		#
		#my $JobNameEncoded = $JobName;
		#$JobNameEncoded =~ s/([^A-Za-z0-9])/sprintf("%%%02X", ord($1))/seg;
		#
		#if ((lc($JobLogType) eq "full") && ($TableRowCount > 0))
		#{
		#	PrintHtml("<TR>");
		#	PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\"><a HREF=\"https://$BaseURL/secure/jobs/scheduledJobView.jsp?contractID=$JobGroupId&jobName=$JobNameEncoded&moduleName=$SolutionIDString&details=&jobRunDetails=&viewMode=jobRun&runNum=$JobRunNumber\"><nbsp>Click here</a> for additional job details.</TD>");
		#	PrintHtml("</TR>");
		#}

		PrintHtml("</TABLE>");

		PrintHtml("<BR>");
	}

	if (lc($JobLogType) eq "full")
	{
		PrintHtml("<CENTER><a href=\"#TOP\"><font size=2>Back to Top</font></a></CENTER>");
		PrintHtml("<BR>");
	}
}

sub PrintSummaryStats() 
{
	my $xferStartTime		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"xferstrttm","");
	my $xferEndTime			= GetStatRecord(\%STATS_SUMMARY_HASH,1,"xferendtm","");
	my $totalElapsedTime      	= ($xferEndTime - $xferStartTime) / 1000000;

	my $totalErrors           	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"te","");
	my $totalUnrecoveredErrors	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"pe","");

	my $totalFilesToTransfer	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"tf","");
	my $totalFilesTransferred	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"ft","");
	my $totalFilesSkipped		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"fs","");
	my $totalFilesDeleted		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"fd","");

	my $totalDirsToTransfer 	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"td","");
	my $totalDirsTransferred	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"dt","");
	my $totalDirsSkipped		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"ds","");
	my $totalDirsDeleted		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"dd","");

	my $totalBytesToTransfer	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"tb","");
	my $totalBytesTransferred	= GetStatRecord(\%STATS_SUMMARY_HASH,1,"fbdata",""); 
	my $totalBytesSkipped		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"fbdatskp","") 
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"fbrsyncskp","");
	my $totalBytesDeleted		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"fbdatdel","");
	my $totalBytesOverhead		= GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohsrcrsync","")
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohtgtrsync","")
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohsrcmnfst","")
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohtgtmnfst","")
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohsrcprtcl","")
								+ GetStatRecord(\%STATS_SUMMARY_HASH,1,"ohtgtprtcl","");
	#
	# Format decimal values for output...
	#
	my $totalKbytesToTransfer = $totalBytesToTransfer / 1024;
	my $totalKbytesTransferred = $totalBytesTransferred / 1024;
	my $totalKbytesSkipped = ($totalKbytesToTransfer - $totalKbytesTransferred);
	my $totalKbytesDeleted = $totalBytesDeleted / 1024;
	my $totalKbytesOverhead = $totalBytesOverhead / 1024;

	if ($totalKbytesSkipped < 0)
	{
		$totalKbytesSkipped = 0;
	}

	my $averageDataRate    = 0;
	my $averageOverallRate = 0;

	$averageDataRate    = ($totalBytesTransferred * 8) / $totalElapsedTime if $totalElapsedTime;
	$averageOverallRate = (($totalBytesTransferred + $totalBytesOverhead) * 8) / $totalElapsedTime if $totalElapsedTime;

	$averageDataRate    =~ s/(\d+\.\d\d).*/$1/;
	$averageOverallRate =~ s/(\d+\.\d\d).*/$1/;

	$totalElapsedTime = TimeFormatted(sprintf("%d",$totalElapsedTime));

	$totalErrors = CommaFormatted(sprintf("%d",$totalErrors));
	$totalUnrecoveredErrors = CommaFormatted(sprintf("%d",$totalUnrecoveredErrors));

	$totalFilesToTransfer = CommaFormatted(sprintf("%d",$totalFilesToTransfer));
	$totalFilesTransferred = CommaFormatted(sprintf("%d",$totalFilesTransferred));
	$totalFilesSkipped = CommaFormatted(sprintf("%d",$totalFilesSkipped));
	$totalFilesDeleted = CommaFormatted(sprintf("%d",$totalFilesDeleted));

	$totalDirsToTransfer = CommaFormatted(sprintf("%d",$totalDirsToTransfer));
	$totalDirsTransferred = CommaFormatted(sprintf("%d",$totalDirsTransferred));
	$totalDirsSkipped = CommaFormatted(sprintf("%d",$totalDirsSkipped));
	$totalDirsDeleted = CommaFormatted(sprintf("%d",$totalDirsDeleted));

	$totalKbytesToTransfer = CommaFormatted(sprintf("%d",$totalKbytesToTransfer));
	$totalKbytesTransferred = CommaFormatted(sprintf("%d",$totalKbytesTransferred));
	$totalKbytesSkipped = CommaFormatted(sprintf("%d",$totalKbytesSkipped));
	$totalKbytesDeleted = CommaFormatted(sprintf("%d",$totalKbytesDeleted));
	$totalKbytesOverhead = CommaFormatted(sprintf("%d",$totalKbytesOverhead));

	$averageDataRate = CommaFormatted($averageDataRate);
	$averageOverallRate = CommaFormatted($averageOverallRate);

	if ($WorkflowContainsSigniantTransfers)
	{
		PrintHtml("<HR>");
		PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
		PrintHtml("<TR><TD CLASS=\"bodyText\">");
		PrintHtml("<a name=\"jobsummarystats\"><b>Total Signiant File Transfer Statistics:</b></a>");
		PrintHtml("</TD></TR>");
		PrintHtml("</TABLE>");
		PrintHtml("<BR>");
		PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
		PrintHtml("<TR><TD CLASS=\"smallLabelText\">Total Signiant File Transfer Statistics for Job: %job_name%</TD></TR>");
		PrintHtml("</TABLE>");

		PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
		$TableRowCount=0;

		if ($totalErrors > 0)
		{
			PrintTableRow(0,"Total Job Errors","$totalErrors");
			PrintTableRow(0,"Total Unrecoverable Job Errors","$totalUnrecoveredErrors");
		}

		PrintTableRow(0,"Total Files to Transfer","$totalFilesToTransfer");
		PrintTableRow(0,"Total Files Transferred","$totalFilesTransferred");
		PrintTableRow(0,"Total Files Skipped","$totalFilesSkipped");
		PrintTableRow(0,"Total Files Deleted","$totalFilesDeleted") if $ConsiderDeletions;
		PrintTableRow(0,"Total Directories to Transfer","$totalDirsToTransfer");
		PrintTableRow(0,"Total Directories Transferred","$totalDirsTransferred");
		PrintTableRow(0,"Total Directories Skipped","$totalDirsSkipped");
		PrintTableRow(0,"Total Directories Deleted","$totalDirsDeleted") if $ConsiderDeletions;
		PrintTableRow(0,"Total Size of Source Data","$totalKbytesToTransfer Kbytes");
		PrintTableRow(0,"Total Data Transferred","$totalKbytesTransferred Kbytes");
		PrintTableRow(0,"Total Data Skipped","$totalKbytesSkipped Kbytes");
		PrintTableRow(0,"Total Data Deleted","$totalKbytesDeleted Kbytes") if $ConsiderDeletions;

		PrintTableRow(0,"Total Job Errors","$totalErrors");

		if ($totalErrors > 0)
		{
			PrintTableRow(0,"Total Unrecoverable Job Errors","$totalUnrecoveredErrors");
		}

		PrintHtml("</TABLE>");
		PrintHtml("<BR>");

		PrintHtml("<CENTER><a href=\"#TOP\"><font size=2>Back to Top</font></a></CENTER>");
		PrintHtml("<BR>");
	}
}

#
# Function: PrintDetailStats
#
# Description: Parse the stats records and print out the detailed totals.
#
sub PrintDetailStats() 
{
	my @KEYS = keys %STATS_AGGREGATE_HASH;
	my $TableDisplayed=FALSE;
	my $IsFileTransfer=FALSE;

	foreach my $KEY ( @KEYS )
	{
		#
		# Get only the last portion of the component name
		#
		my $ComponentName=$KEY;
		my @Cnames = split(/\./,$ComponentName);
		$ComponentName = $Cnames[$#Cnames];

		my %STATSHASH = %{$STATS_AGGREGATE_HASH{$KEY}};

		if (defined(%{$STATSHASH{constants}}))
		{
			my %CONSTS = %{$STATSHASH{constants}};

			#
			# For non-File Transfer components, skip them unless the component has reported work units...
			#
			if (($CONSTS{pkgtype} eq "FILE_TRF")   ||
				($CONSTS{pkgtype} eq "OBJECT_TRF") ||
				($CONSTS{pkgtype} eq "OBJECT_PUT") ||
				($CONSTS{pkgtype} eq "OBJECT_GET"))
			{
				$IsFileTransfer=TRUE;
			}
			else
			{
				if (defined(%{$STATSHASH{summary}}))
				{
					my %SUMMARY = %{$STATSHASH{summary}};

					if (! exists $SUMMARY{twu})
					{
						next;
					}
				}
				else
				{
					if ($KEY ne $ComponentPath){
						SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
						SigPrintLog("Statistics hash 'summary' does not exist for key: $KEY",LOGDEBUG);
					}
					next;
				}

				$IsFileTransfer=FALSE;
			}

			PrintHtml("<HR>");
			PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
			PrintHtml("<TR><TD CLASS=\"bodyText\">");

			if ($IsFileTransfer)
			{
				PrintHtml("<a name=\"$ComponentName\"><b>Signiant File Transfer Statistics for Component: $ComponentName</b></a>");
			}
			else
			{
				PrintHtml("<a name=\"$ComponentName\"><b>Command Statistics for Component: $ComponentName</b></a>");
			}

			PrintHtml("</TD><TR>");
			PrintHtml("</TABLE>");
			PrintHtml("<BR>");

			$TableDisplayed=TRUE;

			if (defined(%{$STATSHASH{reports}}))
			{		
				my %REPORTHASH = %{$STATSHASH{reports}};

				my @SUBKEYS = sort keys %REPORTHASH;

				foreach my $SUBKEY ( @SUBKEYS )
				{
					my %NVPAIRS = %{$REPORTHASH{$SUBKEY}};

					my @PAIRS = keys %NVPAIRS;

					if (exists $NVPAIRS{tgt})
					{
						my $sourceHost = $NVPAIRS{src};
						my $targetHost = $NVPAIRS{tgt};

						my $xferStartTime		= $NVPAIRS{xferstrttm};
						my $xferEndTime			= $NVPAIRS{xferendtm};
						my $totalElapsedTime	= ($xferEndTime - $xferStartTime) / 1000000;

						my $totalErrors				= $NVPAIRS{te};
						my $totalUnrecoveredErrors	= $NVPAIRS{pe};

						my $totalFilesToTransfer	= $NVPAIRS{tf};
						my $totalFilesTransferred	= $NVPAIRS{ft};
						my $totalFilesSkipped		= $NVPAIRS{fs};
						my $totalFilesDeleted		= $NVPAIRS{fd};

						my $totalDirsToTransfer 	= $NVPAIRS{td};
						my $totalDirsTransferred	= $NVPAIRS{dt};
						my $totalDirsSkipped		= $NVPAIRS{ds};
						my $totalDirsDeleted		= $NVPAIRS{dd};

						my $totalBytesToTransfer	= $NVPAIRS{tb};
						my $totalBytesTransferred	= $NVPAIRS{fbdata};
						my $totalBytesSkipped		= $NVPAIRS{fbdatskp} 
													+ $NVPAIRS{fbrsyncskp};
						my $totalBytesDeleted		= $NVPAIRS{fbdatdel};
						my $totalBytesOverhead		= $NVPAIRS{ohsrcrsync}
													+ $NVPAIRS{ohtgtrsync}
													+ $NVPAIRS{ohsrcmnfst}
													+ $NVPAIRS{ohtgtmnfst}
													+ $NVPAIRS{ohsrcprtcl}
													+ $NVPAIRS{ohtgtprtcl};

						my $TotalWorkUnits;
						my $WorkUnitsComplete;
						my $WorkUnitsValue;

						if (! $IsFileTransfer)
						{
							$TotalWorkUnits = $NVPAIRS{twu};
							$WorkUnitsComplete = $NVPAIRS{wuc};
							$WorkUnitsValue = $NVPAIRS{wuv};
						}

						#
						# Format decimal values for output...
						#
						my $totalKbytesToTransfer = $totalBytesToTransfer / 1024;
						my $totalKbytesTransferred = $totalBytesTransferred / 1024;
						my $totalKbytesSkipped = ($totalKbytesToTransfer - $totalKbytesTransferred);
						my $totalKbytesDeleted = $totalBytesDeleted / 1024;
						my $totalKbytesOverhead = $totalBytesOverhead / 1024;

						if ($totalKbytesSkipped < 0)
						{
							$totalKbytesSkipped = 0;
						}

						my $averageDataRate    = 0;
						my $averageOverallRate = 0;

						$averageDataRate    = ($totalBytesTransferred * 8) / $totalElapsedTime if $totalElapsedTime;
						$averageOverallRate = (($totalBytesTransferred + $totalBytesOverhead) * 8) / $totalElapsedTime if $totalElapsedTime;

						$averageDataRate    =~ s/(\d+\.\d\d).*/$1/;
						$averageOverallRate =~ s/(\d+\.\d\d).*/$1/;

						$totalElapsedTime = TimeFormatted(sprintf("%d",$totalElapsedTime));

						$totalErrors = CommaFormatted(sprintf("%d",$totalErrors));
						$totalUnrecoveredErrors = CommaFormatted(sprintf("%d",$totalUnrecoveredErrors));

						$totalFilesToTransfer = CommaFormatted(sprintf("%d",$totalFilesToTransfer));
						$totalFilesTransferred = CommaFormatted(sprintf("%d",$totalFilesTransferred));
						$totalFilesSkipped = CommaFormatted(sprintf("%d",$totalFilesSkipped));
						$totalFilesDeleted = CommaFormatted(sprintf("%d",$totalFilesDeleted));

						$totalDirsToTransfer = CommaFormatted(sprintf("%d",$totalDirsToTransfer));
						$totalDirsTransferred = CommaFormatted(sprintf("%d",$totalDirsTransferred));
						$totalDirsSkipped = CommaFormatted(sprintf("%d",$totalDirsSkipped));
						$totalDirsDeleted = CommaFormatted(sprintf("%d",$totalDirsDeleted));

						$totalKbytesToTransfer = CommaFormatted(sprintf("%d",$totalKbytesToTransfer));
						$totalKbytesTransferred = CommaFormatted(sprintf("%d",$totalKbytesTransferred));
						$totalKbytesSkipped = CommaFormatted(sprintf("%d",$totalKbytesSkipped));
						$totalKbytesDeleted = CommaFormatted(sprintf("%d",$totalKbytesDeleted));
						$totalKbytesOverhead = CommaFormatted(sprintf("%d",$totalKbytesOverhead));

						$averageDataRate = CommaFormatted($averageDataRate);
						$averageOverallRate = CommaFormatted($averageOverallRate);

						#my $PrintPageBreakNow = 1;
						#if ($PrintPageBreakNow)
						#{
						#	PrintHtml("<HR>");
						#}

						#
						# Send total stats to notification messages
						#
						if ($IsFileTransfer)
						{
							PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
							PrintHtml("<TR><TD CELLBORDER=\"0\" WIDTH=\"100%\" CLASS=\"smallLabelText\">Source Agent: $sourceHost</TD></TR>");
							PrintHtml("<TR><TD CELLBORDER=\"0\" WIDTH=\"100%\" CLASS=\"smallLabelText\">Target Agent: $targetHost</TD></TR>");
							PrintHtml("</TABLE>");
						}
						else
						{
							PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
							PrintHtml("<TR><TD CELLBORDER=\"0\" WIDTH=\"100%\" CLASS=\"smallLabelText\">Command Agent: $targetHost</TD></TR>");
							PrintHtml("</TABLE>");
						}

						PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
						$TableRowCount=0;

						if ($IsFileTransfer)
						{
							PrintTableRow(0,"Files to Transfer","$totalFilesToTransfer");
							PrintTableRow(0,"Files Transferred","$totalFilesTransferred");
							PrintTableRow(0,"Files Skipped","$totalFilesSkipped");
							PrintTableRow(0,"Files Deleted","$totalFilesDeleted") if $ConsiderDeletions;
							PrintTableRow(0,"Directories to Transfer","$totalDirsToTransfer");
							PrintTableRow(0,"Directories Transferred","$totalDirsTransferred");
							PrintTableRow(0,"Directories Skipped","$totalDirsSkipped");
							PrintTableRow(0,"Directories Deleted","$totalDirsDeleted") if $ConsiderDeletions;
							PrintTableRow(0,"Size of Source Data","$totalKbytesToTransfer Kbytes");
							PrintTableRow(0,"Data Transferred","$totalKbytesTransferred Kbytes");
							PrintTableRow(0,"Data Skipped","$totalKbytesSkipped Kbytes");
							PrintTableRow(0,"Data Deleted","$totalKbytesDeleted Kbytes") if $ConsiderDeletions;
							PrintTableRow(0,"Overhead Data","$totalKbytesOverhead Kbytes");

							PrintTableRow(0,"Errors","$totalErrors");

							if ($totalErrors > 0)
							{
								PrintTableRow(0,"Unrecoverable Errors","$totalUnrecoveredErrors");
							}

							PrintTableRow(0,"Transfer Time","$totalElapsedTime");
							PrintTableRow(0,"Average Transfer Rate","$averageDataRate bits/second");
							PrintTableRow(0,"Average Transfer Rate (data + overhead)","$averageOverallRate bits/second");
						}
						else
						{	$totalErrors = CommaFormatted(sprintf("%d",$totalErrors));

							PrintTableRow(0,"Total Work Units",CommaFormatted(sprintf("%d",$TotalWorkUnits)));
							PrintTableRow(0,"Work Units Complete",CommaFormatted(sprintf("%d",$WorkUnitsComplete)));
							PrintTableRow(0,"Execution Time","$totalElapsedTime");

							PrintTableRow(0,"Errors","$totalErrors");

							if ($totalErrors > 0)
							{
								PrintTableRow(0,"Unrecoverable Errors","$totalUnrecoveredErrors");
							}
						}

						PrintHtml("</TABLE>");
						PrintHtml("<BR><BR>");
					}
				}
			}
			else
			{
				SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
				SigPrintLog("Statistics hash 'reports' does not exist for key: $KEY",LOGDEBUG);
				PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
				PrintHtml("<TR>");
				PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\">[No Data Available]</TD>");
				PrintHtml("</TR>");
				PrintHtml("</TABLE>");
				PrintHtml("<BR>");
			}
		}
		else
		{
			SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
			SigPrintLog("Statistics hash 'constants' does not exist for key: $KEY",LOGDEBUG);

			PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
			PrintHtml("<TR>");
			PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\">[No Data Available]</TD>");
			PrintHtml("</TR>");
			PrintHtml("</TABLE>");
			PrintHtml("<BR>");
		}

		if ($TableDisplayed)
		{
			PrintHtml("<CENTER><a href=\"#TOP\"><font size=2>Back to Top</font></a></CENTER>");
			PrintHtml("<BR>");
		}
	}
}

#
# Function: GetGmtOffsetSoapRequest
#
# Issues a SOAP request to Signiant web services to obtain the number of offset seconds from GMT
# for the supplied "epoch time" with respect to the supplied timezone.
#
# Returns the requested GMT offset seconds, or returns 'undef' if an error occurs.
#
sub GetGmtOffsetSoapRequest($$)
{
	use SOAP::Lite;
	use Data::Dumper;

	my ($epochTime, $timeZone) = @_;

	my $returnedGmtOffsetSeconds;

	#
	# Issue SOAP request to 'getGmtOffsetSeconds' web service.
	#
	my ($sec,$min,$hr,$day,$mo,$yr) = gmtime($epochTime);
	$mo += 1;
	$yr += 1900;
	my $timeRef = sprintf("%d-%02d-%02dT%02d:%02d:%02d", $yr, $mo, $day, $hr, $min, $sec);

	my $timeRefSoapObj = SOAP::Data->new(name => 'timeref', value => $timeRef, type => "xs:dateTime");

	my $timezoneSoapObj = SOAP::Data->new(name => 'timezone', value => $timeZone, type => "xs:string");

	SigPrintLog("Issuing 'getGmtOffsetSeconds' SOAP request using timeref=$timeRef timezone=$timeZone", LOGDEBUG);

	eval {
		my $SH = SOAP::Lite
				 -> proxy('http://localhost:80/signiant_web_services/TimeUtilsService')
				 -> uri('http://signiant.com/web/services')
				 -> ns('http://signiant.com/web/services')
				 -> readable(1);

			$SH->serializer()->register_ns( 'http://www.w3.org/2001/XMLSchema', 'xs' );

		my $soapResult = $SH -> getGmtOffsetSeconds($timeRefSoapObj, $timezoneSoapObj);

		if (!defined $soapResult) {
			SigPrintLog("Response from 'getGmtOffsetSeconds' web service call is undefined", LOGWARN);
		} elsif ($soapResult->fault) {
			SigPrintLog("Fault during 'getGmtOffsetSeconds' web service call ...", LOGWARN);
			SigPrintLog("Fault Code   : " . $soapResult->faultcode, LOGWARN);
			SigPrintLog("Fault String : " . $soapResult->faultstring, LOGWARN);
			print "%dds_msg_info%Fault Detail : " . Dumper($soapResult->faultdetail) . "\n";
		} else {
			my $responseValue = $soapResult->valueof('//getGmtOffsetSecondsResponse/return');
			if ($responseValue =~ /^[-+]?[0-9]+$/) {
				$returnedGmtOffsetSeconds = $responseValue;
				SigPrintLog("Response from 'getGmtOffsetSeconds' SOAP request was ($returnedGmtOffsetSeconds)", LOGDEBUG);
			} else {
				SigPrintLog("Response from 'getGmtOffsetSeconds' web service call was non-integer ($responseValue)", LOGWARN);
			}
		}
	};

	if ($@) {
		my $ErrorMessage = $@;
		chomp ($ErrorMessage);
		SigPrintLog("Exception during 'getGmtOffsetSeconds' web service call ($ErrorMessage)", LOGWARN);
	}

	return $returnedGmtOffsetSeconds;
}

#
# Function: FetchGmtOffsetForReporting
#
# The initial invocation determines the number of offset seconds from GMT for the supplied
# "epoch time" with respect to the timezone specified in the $ReportingTimezone global.
# For all but the default GMT case, a SOAP message will be sent to Signiant web services to
# request the required value. The resultant value will be stored for subsequent invocations.
#
# Subsequent invocations simply return the value stored during the initial invocation.
#
# Any errors in the SOAP request/response cycle will cause a value of 0 to be stored, and
# the value of the $ReportingTimezone global to be re-assigned to "GMT".
#

my $StoredGmtOffsetSeconds;		## GMT offset seconds value is stored once only

sub FetchGmtOffsetForReporting($)
{
	return $StoredGmtOffsetSeconds if (defined($StoredGmtOffsetSeconds));

	$StoredGmtOffsetSeconds = 0;	## assign "default" offset in case TZ=GMT or SOAP request fails

	if ($ReportingTimezone ne "GMT") {
		my $gmtOffset = GetGmtOffsetSoapRequest(@_[0], $ReportingTimezone);
		if (defined($gmtOffset)) {
			$StoredGmtOffsetSeconds = $gmtOffset;
		} else {
			$ReportingTimezone = "GMT";
		}
	}

	return $StoredGmtOffsetSeconds;
}

#
# Function: PrintWorkflowSummary
#
# Description:
#
sub PrintWorkflowSummary()
{
	my $ComponentName;
	my $ComponentType;
	my $StartDateTime;
	my $EndDateTime;
	my $ElapsedTime;

	my $ValueTextFormat;
	my $TableRowCount=0;

	my $gmtOffsetSeconds = FetchGmtOffsetForReporting(time());

	PrintHtml("<HR>");
	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
	PrintHtml("<TR><TD CLASS=\"bodyText\">");
	PrintHtml("<a name=\"workflowsummary\"><b>Workflow Summary:</b></a>");
	PrintHtml("</TD></TR>");
	PrintHtml("</TABLE>");
	PrintHtml("<BR>");
	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
	PrintHtml("<TR>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">Workflow Component</TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">Component Type</TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">Start Date/Time<font size=1> ($ReportingTimezone)</font></TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">End Date/Time<font size=1> ($ReportingTimezone)</font></TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">Duration</TD>");
	PrintHtml("</TR>");

	my @KEYS = keys %STATS_AGGREGATE_HASH;

	foreach my $KEY ( @KEYS )
	{
		my %STATSHASH = %{$STATS_AGGREGATE_HASH{$KEY}};

		if (defined(%{$STATSHASH{constants}}))
		{
			my %CONSTS = %{$STATSHASH{constants}};

			$ComponentType = $CONSTS{pkgtype};

			if ($ComponentType eq "GROUP")
			{
				$ComponentType = "Component Group";
			}
			elsif ($ComponentType eq "REMOTE_CMD")
			{
				$ComponentType = "Command";
			}
			elsif ($ComponentType eq "FILE_TRF")
			{
				$ComponentType = "Signiant File Transfer";
			}
			elsif ($ComponentType eq "OBJECT_TRF")
			{
				$ComponentType = "Signiant Object Transfer";
			}
			elsif ($ComponentType eq "OBJECT_PUT")
			{
				$ComponentType = "Signiant Object Upload";
			}
			elsif ($ComponentType eq "OBJECT_GET")
			{
				$ComponentType = "Signiant Object Download";
			}
			elsif ($ComponentType eq "PROCESS_TRF")
			{
				$ComponentType = "Process Transfer";
			}
			elsif ($ComponentType eq "PKG_LINK")
			{
				$ComponentType = "Decision Link";
			}

			#
			# Get only the last portion of the component name
			#
			$ComponentName=$KEY;
			my @Cnames = split(/\./,$ComponentName);
			$ComponentName = $Cnames[$#Cnames];

			#print "Component Name: $ComponentName\n\n";
			#
			#my @SUBKEYS = sort keys %CONSTS;
			#
			#foreach my $SUBKEY ( @SUBKEYS )
			#{
			#	print "Name $SUBKEY ==> Value = $CONSTS{$SUBKEY}\n";
			#}

			$StartDateTime = gmtime(sprintf("%d", ($CONSTS{"agtstrttm"} / 1000000) + 0.5) + $gmtOffsetSeconds);

			if (defined(%{$STATSHASH{summary}}))
			{
				my %SUMMARY = %{$STATSHASH{summary}};

				#print "**PRINTSTATSRECORDS: Summary for $KEY\n\n";
				#
				#my @SUBKEYS = sort keys %SUMMARY;
				#
				#foreach my $SUBKEY ( @SUBKEYS )
				#{
				#	print "Name $SUBKEY ==> Value = $SUMMARY{$SUBKEY}\n";
				#}

				$EndDateTime = gmtime(sprintf("%d", ($SUMMARY{"agtendtm"} / 1000000) + 0.5) + $gmtOffsetSeconds);

				$ElapsedTime = TimeFormatted(sprintf("%d", (($SUMMARY{"agtendtm"} - $CONSTS{"agtstrttm"}) / 1000000) + 0.5));
			}
			else
			{
				if ($KEY ne $ComponentPath){
					SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
					SigPrintLog("Statistics hash 'summary' does not exist for key: $KEY",LOGDEBUG);
				}
			}

			if ($TableRowCount % 2 == 0)
			{
				$ValueTextFormat="verySmallValueText";
			}
			else
			{
				$ValueTextFormat="verySmallValueText2";
			}

			PrintHtml("<TR>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"20%\">$ComponentName</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"20%\">$ComponentType</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"20%\">$StartDateTime</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"20%\">$EndDateTime</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"20%\">$ElapsedTime</TD>");
			PrintHtml("</TR>");

			$TableRowCount++;
		}
		else
		{
			SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
			SigPrintLog("Statistics hash 'constants' does not exist for key: $KEY",LOGDEBUG);
		}

		if (defined(%{$STATSHASH{reports}}))
		{
			my %REPORTHASH = %{$STATSHASH{reports}};

			my @SUBKEYS = keys %REPORTHASH;

			#foreach my $SUBKEY ( @SUBKEYS )
			#{
			#	print "\nSource-Target Report - $SUBKEY\n\n";
			#
			#	my %NVPAIRS = %{$REPORTHASH{$SUBKEY}};
			#
			#	my @PAIRS = keys %NVPAIRS;
			#
			#	foreach my $PVKEY ( @PAIRS )
			#	{
			#		print "Name $PVKEY ==> Value = $NVPAIRS{$PVKEY}\n";
			#	}
			#}
		}
		else
		{
			SigPrintLog("Incomplete statistics found for job. Possible causes are a cancelled job or an unreachable agent.",LOGWARN);
			SigPrintLog("Statistics hash 'reports' does not exist for key: $KEY",LOGDEBUG);
		}
	}

	PrintHtml("</TABLE>");
	PrintHtml("<BR>");

	PrintJobLog("error_warning","","Error and Warning Messages");

	PrintHtml("<CENTER><a href=\"#TOP\"><font size=2>Back to Top</font></a></CENTER>");
	PrintHtml("<BR>");
}

sub GetDeliveryLogFile()
{
	my $FileFound=0;

	my $DeliveryLogDir = "$DTMLogDir/delivery_logs";

	find(\&FindDeliveryLogFile, $DeliveryLogDir);
}

sub FindDeliveryLogFile()
{     
	my $CurrentFile = $File::Find::name;

	my $FileNamePattern = $JobName . "_" . $JobID;

	if ( $CurrentFile =~ /$FileNamePattern/ )
	{
		$DeliveryLogFilePath = $CurrentFile;
	}
}

sub PrintDeliveryLog()
{
	use HTTP::Date;		# for date/time string conversions

	GetDeliveryLogFile();

	chomp( $DeliveryLogFilePath );

	if ($^O eq "MSWin32")
	{
		$DeliveryLogFilePath =~ s/\//\\/g;
	}
	else
	{
		$DeliveryLogFilePath =~ s/\\/\//g;
	}

	$TableRowCount=0;
	$MaxTableRows=500;

	PrintHtml("<HR>");
	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"0\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
	PrintHtml("<TR><TD CLASS=\"bodyText\">");

	PrintHtml("<a name=\"deliverylog\"><b>Signiant File Delivery Log</b></a>");

	PrintHtml("</TD></TR>");
	PrintHtml("</TABLE>");
	PrintHtml("<BR>");

	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
	PrintHtml("<TR><TD CLASS=\"smallLabelText\">Signiant File Delivery Log for Job: %job_name%</TD></TR>");
	PrintHtml("</TABLE>");

	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");

	my $gmtOffsetSeconds = FetchGmtOffsetForReporting(time());

	PrintHtml("<TR>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"20%\">Date/Time<font size=1> ($ReportingTimezone)</font></TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"15%\">Source Agent</TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"15%\">Target Agent</TD>");
	PrintHtml("<TD CLASS=\"verySmallLabelText\"WIDTH=\"65%\">File Name</TD>");
	PrintHtml("</TR>");

	if (open(DELIVERYLOG,$DeliveryLogFilePath))
	{
		my $SourceAgent;
		my $TargetAgent;
		my $Filename;
		my $ValueTextFormat;

		while (!eof(DELIVERYLOG) && ($TableRowCount < $MaxTableRows))
		{
			my $logLine = <DELIVERYLOG>;

			$logLine =~ s/[\n\r]//g;

			my ($dateTime, $dummy) = split(/-/, $logLine);
			next if ($dateTime =~ /CERT: /);	## skip this line if it's a CERTIFICATE line

			# Convert date/time from 'HH:MM:SS Month Day, YYYY' delivery log format to canonical 'YYYY/MM/DD HH:MM:SS' format
			#
			$dateTime =~ m/^(\S+)\s(\S+)\s(\d+),\s(\d+)/;
			my $epochTime = HTTP::Date::str2time("$3 $2 $4 $1", "GMT");
			$epochTime += $gmtOffsetSeconds;	# convert to local timezone
			my ($sec, $min, $hr, $day, $mon, $yr) = gmtime($epochTime);
			$dateTime = sprintf("%04d/%02d/%02d %02d:%02d:%02d", ($yr+1900), ($mon+1), $day, $hr, $min, $sec);

			if ($logLine =~ m/\"(.+)\"/) {
				$Filename = $1;
			}

			if ($logLine =~ m/src=([^ ]+)/) {
				$SourceAgent = $1;
			}

			if ($logLine =~ m/tgt=([^ ]+)/) {
				$TargetAgent = $1;
			}

			if ($TableRowCount % 2 == 0) {
				$ValueTextFormat="verySmallValueText";
			} else {
				$ValueTextFormat="verySmallValueText2";
			}

			PrintHtml("<TR>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"15%\">$dateTime</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"15%\">$SourceAgent</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"15%\">$TargetAgent</TD>");
			PrintHtml("<TD CLASS=\"$ValueTextFormat\"WIDTH=\"65%\">$Filename</TD>");
			PrintHtml("</TR>");

			$TableRowCount++;
		}

		close(DELIVERYLOG);
	}

	PrintHtml("</TABLE>");

	PrintHtml("<TABLE RULES=\"cols\" BORDER=\"1\" WIDTH=\"100%\" CELLPADDING=\"3\" CELLSPACING=\"0\">");
	PrintHtml("<TR>");

	if ($TableRowCount == 0)
	{
		PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\">[No Files Delivered or Delivery Logging Disabled]</TD>");
	}
	elsif ($TableRowCount >= $MaxTableRows)
	{
		PrintHtml("<TD align=center CLASS=\"verySmallLabelText\"WIDTH=\"100%\">[Log truncated at $MaxTableRows lines]</TD>");
	}

	PrintHtml("</TR>");
	PrintHtml("</TABLE>");
	PrintHtml("<BR>");

	PrintHtml("<CENTER><a href=\"#TOP\"><font size=2>Back to Top</font></a></CENTER>");
	PrintHtml("<BR>");
}

sub URLEncode($)
{
	my $SourceURL = @_[0];
	$SourceURL =~ s/([^A-Za-z0-9])/sprintf("%%%02X", ord($1))/seg;
	return($SourceURL);
}

#
# Return the array of report files under the given directory
#
sub BuildReportFileList($)
{
	my ($PARENTDIR) = @_;
	my $DIRENTRY;
	my @FILELIST;
	my $ReportPath;
	my %REPORTFILEHASH;

	if ( opendir ( HDIR, $PARENTDIR ) )
	{
		while ( $DIRENTRY = readdir( HDIR ) )
		{
			$ReportPath = "$PARENTDIR" . $SLASH . "$DIRENTRY";

			# Only count regular files
			if ( FileExists($ReportPath) )
			{
				if ($DIRENTRY =~ m/\.html$/)
				{
					my($dev,$ino,$mode,$nlink,$uid,$gid,$rdev,$size,$atime,$mtime,$ctime,$blksize,$blocks) = stat($ReportPath);
					$REPORTFILEHASH{$ctime}=$DIRENTRY;
				} 
			}
		} 

		closedir HDIR;

		#
		# Sort the list from oldest (first) to newest (last)
		#
		foreach my $key (sort keys %REPORTFILEHASH)
		{
			push(@FILELIST,$REPORTFILEHASH{$key});
		}
	}
	else
	{
		if ($ReportVersions > 0)
		{
			SigPrintLog("ERROR: BuildReportFileList- Failed to open directory: $PARENTDIR",LOGERROR);
		}
	}

	return @FILELIST;
}

#
# Trim the report directory...
#
sub TrimReportDir($@$)
{
	my ($PARENTDIR) = $_[0];
	my (@FILELIST) = @{$_[1]};
	my ($VERSIONSTOKEEP) = $_[2];
	my ($DIRTYPE) = $_[3];
	my $LISTSIZE = @FILELIST;
	my $REMOVEPATH="";
	my $REMOVEDIR="";
	my $FILESREMOVED=0;

	#
	# Remove any log directories older than specified versions to keep
	#    
	SigPrintLog("Trimming $DIRTYPE directory: $PARENTDIR...",LOGDEBUG);
	SigPrintLog("Number of $DIRTYPE versions present: $LISTSIZE",LOGDEBUG);
	SigPrintLog("Number of $DIRTYPE versions to keep: $VERSIONSTOKEEP",LOGDEBUG);

	if ( $LISTSIZE > $VERSIONSTOKEEP )
	{
		#
		# The filelist array is already sorted with oldest entries at the beginning...
		#
		for ( my $i = 0; $i < ($LISTSIZE - $VERSIONSTOKEEP); ++$i )
		{
			if ( FileExists("$PARENTDIR" . $SLASH . "$FILELIST[$i]") )
			{
				$REMOVEPATH = "$PARENTDIR" . $SLASH . "$FILELIST[$i]";
				SigPrintLog("Removing $DIRTYPE version: $REMOVEPATH",LOGDEBUG);
				unlink($REMOVEPATH);
			}
		}

		if ($VERSIONSTOKEEP == 0)
		{
			File::Path::rmtree($PARENTDIR);
		}
	}   

	SigPrintLog("Completed $DIRTYPE directory trim.",LOGDEBUG);
}

sub GenerateReport()
{
	WriteHtmlHeader(0);

	PrintReportHeader();

	PrintWorkflowSummary();

	PrintSummaryStats();

	PrintDetailStats();

	if ($WorkflowContainsSigniantTransfers)
	{
		PrintDeliveryLog();
	}

	WriteHtmlFooter(0);

	return(0);
}


#
# Input: 1: Stats Hash
#	2: Hash Type (1==Summary, 2==Report)
#	3: Stat Type
#	4: Record Qualifier (used for report hash only - to specify the source/target host)
#
sub GetStatRecord ( $$$$ )
{
	my %HASH = %{$_[0]};
	my $HashType = $_[1];
	my $StatType = $_[2];
	my $Qualifier = $_[3];

	my $RecordValue = 0;

	if ($HashType == 1)
	{
		my @KEYS = keys %HASH;

		foreach my $KEY ( @KEYS )
		{
			my %STATSHASH = %{$HASH{$KEY}};

			my %SUMMARYHASH = %{$STATSHASH{summary}};

			my @SUBKEYS = keys %SUMMARYHASH;

			if (exists $SUMMARYHASH{$StatType})
			{
				$RecordValue = $SUMMARYHASH{$StatType};
				last;
			}
		}
	}
	elsif ($HashType == 2)
	{
		my @KEYS = keys %HASH;

		foreach my $KEY ( @KEYS )
		{
			my %STATSHASH = %{$HASH{$KEY}};

			my %REPORTHASH = %{$STATSHASH{reports}};

			my @SUBKEYS = keys %REPORTHASH;

			foreach my $SUBKEY ( @SUBKEYS )
			{
				my %NVPAIRS = %{$REPORTHASH{$SUBKEY}};

				if ((exists $NVPAIRS{tgt}) && ($NVPAIRS{tgt} eq $Qualifier))
				{
					my @PAIRS = keys %NVPAIRS;

					foreach my $PVKEY ( @PAIRS )
					{
						if (exists $NVPAIRS{$StatType})
						{
							$RecordValue = $NVPAIRS{$StatType};
							last;
						}
					}
				}
			}
		}
	}

	return $RecordValue;
}

sub PrintStatsRecords ( $$ )
{
	my %HASH = %{$_[0]};
	my $StatType = $_[1];

	my @KEYS = keys %HASH;

	foreach my $KEY ( @KEYS )
	{
		my %STATSHASH = %{$HASH{$KEY}};

		if ($StatType == 0 || $StatType == 1)
		{
			print "PRINTSTATSRECORDS: Statistics for KEY: $KEY - Possible incomplete job.\n\n";

			my %CONSTS = %{$STATSHASH{constants}};

			print "PRINTSTATSRECORDS: Constants for $KEY\n\n";

			my @SUBKEYS = keys %CONSTS;

			foreach my $SUBKEY ( @SUBKEYS )
			{
				print "Name $SUBKEY ==> Value = $CONSTS{$SUBKEY}\n";
			}
		}

		if ($StatType == 0 || $StatType == 2)
		{
			print "\nPRINTSTATSRECORDS: Summary for $KEY\n\n";

			my %SUMMARY = %{$STATSHASH{summary}};

			my @SUBKEYS = keys %SUMMARY;

			foreach my $SUBKEY ( @SUBKEYS )
			{
				print "Name $SUBKEY ==> Value = $SUMMARY{$SUBKEY}\n";
			}
		}

		if ($StatType == 0 || $StatType == 3)
		{
			print "\nPRINTSTATSRECORDS: Reports for $KEY\n\n";

			my %REPORTHASH = %{$STATSHASH{reports}};

			my @SUBKEYS = keys %REPORTHASH;

			foreach my $SUBKEY ( @SUBKEYS )
			{
				print "\nSource-Target Report - $SUBKEY\n\n";

				my %NVPAIRS = %{$REPORTHASH{$SUBKEY}};

				my @PAIRS = keys %NVPAIRS;

				foreach my $PVKEY ( @PAIRS )
				{
					print "Name $PVKEY ==> Value = $NVPAIRS{$PVKEY}\n";
				}
			}
		}
	}
}

sub EmailReport()
{
	my $dds_home= GetBaseDDSInstallDirectory();

	my $RET=0;

	#
	# Pull the Scheduler name and e-mail address from the Signiant.ini
	# for the e-mail sent to the target user so that it has the same   
	# from address as the notification sent from the scheduler to the
	# originator of the package. Originally the e-mail from address 
	# was spoofed to look like it was coming from the originator,
	# but this was being rejected by e-mail transfer agents. 
	#

	my $commonlibPath = $dds_home . "/bin/CommonMisc.pl";
	my $iniFile = $dds_home . "/web/signiant.ini";

	require $commonlibPath;
	my %ini = CommonMisc->readConfig($iniFile);

	my $FromUser = $ini{'SCHDSVR_SUPPORT_TITLE'};
	my $FromSource = $ini{'SCHDSVR_SUPPORT_EMAILADDR'};

	my @mailMsg;

	if ($SendReportCheck)
	{
            $EmailJobFailureReportTo =~ s/&/&amp;/g;
            push @mailMsg, "To: ". $EmailJobFailureReportTo . "\n";
	}
	else
	{
            $EmailTo =~ s/&/&amp;/g;
            $EmailCc =~ s/&/&amp;/g;
            $EmailBcc =~ s/&/&amp;/g;
            push @mailMsg, "To: ". $EmailTo . "\n";
            push @mailMsg, "Cc: ". $EmailCc . "\n";
            push @mailMsg, "Bcc: ". $EmailBcc . "\n";
	}     

	if ($ErrorsOccurred && !$jobSucceeded)
	{
            push @mailMsg, "Subject: FAILURE: $EmailSubject\n";
            $EmailSubject = "FAILURE: ". $EmailSubject;
	}
	elsif ($ErrorsOccurred && $jobSucceeded)
	{
            if(lc($EmailCondition) eq "on transfer"){
                 push @mailMsg, "Subject: SUCCESS: $EmailSubject\n";
                 $EmailSubject = "SUCCESS: ". $EmailSubject;
            }else{
                 push @mailMsg, "Subject: SUCCESS: $EmailSubject (with recoverable errors)\n";
                 $EmailSubject = "SUCCESS: ". $EmailSubject . " (with recoverable errors)";
            }
	}
	else
	{
            push @mailMsg, "Subject: SUCCESS: $EmailSubject\n";
            $EmailSubject = "SUCCESS: ". $EmailSubject;
	}

    # 
	# old from address use to spoof the originator name, but this
	# was being rejected by MTAs (See Jira issue PR-12355)
	# Old format:
	# push @mailMsg, "From: \"$UserNameSource\" <" . $EmailSource . ">\n";
    #
    push @mailMsg, "From: \"$FromUser\" <" . $FromSource . ">\n";
    push @mailMsg, "MIME-Version: 1.0\n";
    push @mailMsg, "Content-Type: text/html; charset=utf-8\n";
    push @mailMsg, "Content-Transfer-Encoding: 8bit\n";
    push @mailMsg, "Content-Disposition: inline\n\n";

    push @mailMsg, "$HtmlReportString\n";

    $RET = SendEmailMessage(\@mailMsg, $EmailSubject, $HtmlReportString );

	return($RET);
}

sub SendTraps()
{
	my $SigniantEnterpriseOID = "1.3.6.1.4.1.10232";
	my $SNMPTrapEnterpriseID="10232";
	my $SNMPTrapID;
	my $VarBinds = "";
	my $TrapText = "";
	my $RET;

	if ($ErrorsOccurred)
	{
		$SNMPTrapID = 1; # genericJobFailure
		$TrapText = "FAILURE: Signiant Job '$JobName' (run #$JobRunNumber)";
	}
	else
	{
		$SNMPTrapID = 2; # genericJobSuccess
		$TrapText = "SUCCESS: Signiant Job '$JobName' (run #$JobRunNumber)";
	}

	$VarBinds .= "$SigniantEnterpriseOID.1000.1.1 s \"$TrapText\" ";
	$VarBinds .= "$SigniantEnterpriseOID.1000.1.2 s \"$ManagerHost\" ";
	$VarBinds .= "$SigniantEnterpriseOID.1000.1.3 s \"$JobGroupId\" ";
	$VarBinds .= "$SigniantEnterpriseOID.1000.1.4 s \"$JobName\" ";
	$VarBinds .= "$SigniantEnterpriseOID.1000.1.5 s \"$JobRunNumber\" ";

	$RET = SolutionSNMPSendTrap($SNMPTrapID,$TrapText,$SNMPValidTrapReceivers,$SNMPTrapCommunityString,$SNMPTrapEnterpriseID,$VarBinds);

	return($RET);
}

sub SetOutputs()
{
	# Strip out all newlines from the HTML before setting the output...
	$HtmlReportString =~ s/\n//g;
	SigSetOutput("Outputs.HTMLReportString",$HtmlReportString);
	SigSetOutput("Outputs.ReportFilePath",$ReportFile);
	SigSetOutput("Outputs.WorkflowAggregateStats",$STATS_AGGREGATE_STR);

	if ($RC == 0)
	{
		SigSetOutput("Outputs.ReturnCode",0);
		SigSetOutput("Outputs.ReturnMessage","Job Report Component Completed Successfully");
	}
	else
	{
		SigSetOutput("Outputs.ReturnCode",$RC);
		SigSetOutput("Outputs.ReturnMessage","Job Report Component Completed With Errors");
	}
}

sub psqlOutFile($)
{
  my $sql = $_[0];

  my $psqlOutfile = File::Spec->catfile($TempDir,"psql_$$.out");
  my $psqlInfile  = File::Spec->catfile($TempDir,"psql_$$.in");

  open(PSQLIN,">$psqlInfile") or die ("Failed to open file: '$psqlInfile' - $!");
  print PSQLIN $sql;
  close(PSQLIN);

  my $cmd= "\"$PSQL\" -v \"ON_ERROR_STOP=1\" -t -A -f $psqlInfile -U $DBUSER -d $DBNAME -o $psqlOutfile";

  SigPrintLog("DEBUG: system command to execute is: $cmd", LOGDEBUG);
  SigPrintLog("DEBUG: sql to execute is: $sql", LOGDEBUG);      

  my $rc = system($cmd);
  if ( $rc != 0 ) {
    PrintActivity("DB command failed: $cmd - $!", LOGERROR); 
    exitFailure(1);
  }
  unlink($psqlInfile) if (! $DEBUG);		# unlink psqlInfile if not debug
  return($psqlOutfile);
}

sub checkForConsecutiveFailures()
{
  my @numbers = ();

  for(my $i=1;$i<$ConsecutiveFailures ;$i++){
   push @numbers,$JobRunNumber -$i;
  }

  push @numbers,0 if scalar @numbers == 0;

  my $outFile = psqlOutFile("select count(*) from scheduled_job_run as sjr where sjr.deleted = 0 and sjr.exit_code > 0 and sjr.exit_code <> 20 and sjr.job_name='" . $JobName  . "' and sjr.contract_id=" . $JobGroupId . " and sjr.run_number in (" . join(",", @numbers) . ")");

  my $countFailedJobs= "";

  if (!open(PSQLOUT, $outFile)){
    SigPrintLog("Failed to open internal file '$outFile' - $!", LOGERROR);
  }
  else
  {
    while (<PSQLOUT>){
	  (my $data= $_) =~ s/^\s+|\s+$//g;	# strip leading/trailing whitespace
	   next if ($data eq ''); 
	   $countFailedJobs = $data; 
    }
    close(PSQLOUT);
  }

  if($countFailedJobs >= $ConsecutiveFailures-1){
   return true;
  }
  else
  {
   return false;
  }
}

sub sendEmail()
{
  if  ((lc($EmailCondition) eq "always" ) ||
    ((lc($EmailCondition) eq "always exclude empty transfers") && ($FilesTransferred)) || 
    ((lc($EmailCondition) eq "on transfer") && ($FilesTransferred || $FilesChanged || $WorkUnitsCompleted)) ||
    ((lc($EmailCondition) eq "on error") && ($ErrorsOccurred)) ||
     (lc($EmailCondition) eq ""))
{
  	SigPrintLog("Generating job report...",LOGINFO);

  	$REPORT_RC = GenerateReport();

	if ($ReportVersions > 0)
	{
		CreateReportFile();
	}

	if ($REPORT_RC == 0)
	{
		SigPrintLog("Job report generated successfully.",LOGINFO);
	}
	else
	{
		SigPrintLog("ERROR: Errors occurred while generating job report.",LOGERROR);
	}

	SigPrintLog("Emailing job report...",LOGINFO);

	if($EmailJobFailureReportTo eq "" || $jobSucceeded || (!$jobSucceeded && (lc($EmailCondition) eq "on error"))){

	   if($EmailJobFailureReportTo ne $EmailTo || $ErrorsOccurred == 0) {
			@EmailToArray = split(/\;/, $EmailTo);
			@EmailCcArray = split(/\;/, $EmailCc);
			for my $j (0 .. $#EmailCcArray) {
				push @EmailToArray , $EmailCcArray[$j];
			}
			@EmailBccArray = split(/\;/, $EmailBcc);
			for my $k (0 .. $#EmailBccArray) {
				push @EmailToArray , $EmailBccArray[$k];
			}

			@EmailJobFailureReportToArray = split(/\;/, $EmailJobFailureReportTo);
			for my $i (0 .. $#EmailToArray) {

			@del_indexes = reverse(grep { $EmailJobFailureReportToArray[$_] eq $EmailToArray[$i] } 0..$#EmailJobFailureReportToArray);

				foreach $item (@del_indexes) {
					splice (@EmailJobFailureReportToArray,$item,1);
				}
			}
			$EmailJobFailureReportTo = join(';',@EmailJobFailureReportToArray);
			$SendReportCheck=0;	   
			$EMAIL_RC=EmailReport();
	   }
	}

	if(!$EmailJobFailureReportTo eq "" && (!$jobSucceeded || ($ErrorsOccurred && $jobSucceeded))){
		@EmailToArray = split(/\;/, $EmailTo);
		@EmailCcArray = split(/\;/, $EmailCc);
		for my $j (0 .. $#EmailCcArray) {
			push @EmailToArray , $EmailCcArray[$j];
		}
		@EmailBccArray = split(/\;/, $EmailBcc);
		for my $k (0 .. $#EmailBccArray) {
			push @EmailToArray , $EmailBccArray[$k];
		}
		@EmailJobFailureReportToArray = split(/\;/, $EmailJobFailureReportTo);
		for my $i (0 .. $#EmailToArray) {

			@del_indexes = reverse(grep { $EmailJobFailureReportToArray[$_] eq $EmailToArray[$i] } 0..$#EmailJobFailureReportToArray);

			foreach $item (@del_indexes) {
				splice (@EmailJobFailureReportToArray,$item,1);
			}
		}
		$EmailJobFailureReportTo = join(';',@EmailJobFailureReportToArray);
		if($EmailJobFailureReportTo ne ""){ 
			 $SendReportCheck=1;
			 $EMAIL_RC=EmailReport();
		 }else{
			 $SendReportCheck=0;	   
			 $EMAIL_RC=EmailReport();
		 }
	}

	if ($EMAIL_RC == 0)
	{
		SigPrintLog("Job report emailed successfully.",LOGINFO);
	}
	else
	{
		SigPrintLog("ERROR: Errors occurred while emailing job report.",LOGERROR);
	}

	@REPORTFILELIST = BuildReportFileList($ReportPath);

	TrimReportDir($ReportPath,\@REPORTFILELIST ,$ReportVersions,"report");

	$RC = $RC + $REPORT_RC + $EMAIL_RC;
  }
}

#
# MAIN...
#

SigPrintLog("Executing JobReport component: %job_template%",LOGINFO);

Initialize();

LogVars();

ValidateInputs();

GetStats();

CheckFilesTransferredAndErrorsOccurred($RC);

if (((lc($SNMPTrapTypes) =~ m/success/) && !$ErrorsOccurred) ||
    ((lc($SNMPTrapTypes) =~ m/failure/) && $ErrorsOccurred))
{
	SigPrintLog("Generating SNMP traps...",LOGINFO);

	$TRAP_RC = SendTraps();

	if ($TRAP_RC == 0)
	{
		SigPrintLog("SNMP traps generated successfully.",LOGINFO);
	}
	else
	{
		SigPrintLog("ERROR: Errors occurred while generating SNMP traps",LOGERROR);
	}

	$RC = $RC + $TRAP_RC;
}

CheckFilesTransferredAndErrorsOccurred($RC);

my $checkToSendEmail = true;

if($ConsecutiveFailures > 1){
 $checkToSendEmail = checkForConsecutiveFailures();
}

if($checkToSendEmail){
  sendEmail();
}

DoExit:

	SetOutputs();

	if ($RC == 0)
	{
		SigPrintLog("Job Report component completed successfully",LOGINFO);
	}
	else
	{
		SigPrintLog("Job Report component completed with errors",LOGERROR);
	}

	exit($RC); 

