#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionSigListXML%
%script_lib_obj:SolutionNotify%
%script_lib_obj:SolutionModifyInputValue%
%script_lib_obj:SolutionSplitPaths%

SigPrintLog("DEBUG: Executing 'Source Initialization Command' inside 'SigniantObjectUpload' component.", LOGDEBUG);

use JSON;
use Data::Dumper;

#
# Initialize globals...
#

my $SUPERDEBUG=0;

#
# Get the current working directory...this will either be /tmp or whatever the temp
# directory has been set to.
#
chomp(my $DdsSourceTempDir = `dds_cfgutil temporarydirectory`);
print "%dds_set%DdsSourceTempDir=$DdsSourceTempDir\n";

my $JobDateTimeStamp=ModifyInputValue(q|%JobDateTimeStamp% |,0);

my $SourceAgent = ModifyInputValue(q|%src_agent% |,0);
my $SourceAgentVersion = ModifyInputValue(q|%agent_release_version% |,0);
my $SourceAgentBuildNumber = ModifyInputValue(q|%agent_build_identifier% |,0);
my $SourceAgentPlatform = ModifyInputValue(q|%src_platform% |,0);
my $SourceAgentUserid = ModifyInputValue(q|%src_user% |,0);

my $SourceHost = ModifyInputValue(q|%Inputs.Source.src_node_list% |,0);
my $IncludeFiles = ModifyInputValue(q|%Inputs.Source.IncludeFiles% |,0);
my $ExcludeFiles = ModifyInputValue(q|%Inputs.Source.ExcludeFiles% |,0);
my $ExcludeSubdirectories = ModifyInputValue(q|%Inputs.Source.ExcludeSubdirectories% |,0);
my $ExcludeSubDirList = ModifyInputValue(q|%Inputs.Source.ExcludeSubDirList% |,0);
my $TargetHosts = ModifyInputValue(q|%Inputs.Target.tgt_node_list% |,0);
my $TargetObjectStorage = from_json(ModifyInputValue(q|%Inputs.Target.targetObjectStorage% |,0));
my $PathMappingOptions = ModifyInputValue(q|%Inputs.Target.PathMappingOptions% |,0);
my $MaximumJobDateTimeDirectories = ModifyInputValue(q|%Inputs.Target.maximumJobDateTimeDirectories% |,0);
my $RemoveParentFolder = ModifyInputValue(q|%Inputs.Options.RemoveParentFolder% |,0);
my $SourceDeletionsPostTransfer = ModifyInputValue(q|%Inputs.Source.SourceDeletionsPostTransfer% |,0);
my $SourceDeletionExcludeDirectories = ModifyInputValue(q|%Inputs.Source.SourceDeletionExcludeDirectories% |,0);
my $MaximumBandwidthLimit = ModifyInputValue(q|%Inputs.Transport.bw_throttle_tod% |,0);
my $WANAcceleratorOn = ModifyInputValue(q|%Inputs.Transport.udp_transfer% |,0);
my $WANAcceleratorAggressiveness = ModifyInputValue(q|%Inputs.Transport.udp_aggressiveness% |,0);
my $BandwidthCeiling = ModifyInputValue(q|%Inputs.Transport.throughput_tgt% |,0);
my $BandwidthFloor = ModifyInputValue(q|%Inputs.Transport.bw_floor% |,0);
my $CompressFiles = ModifyInputValue(q|%Inputs.Options.compression% |,0);
my $FollowSymbolicLinks = ModifyInputValue(q|%Inputs.Options.follow_symlinks% |,0);
my $FileReadinessCheckType = ModifyInputValue(q|%Inputs.Source.FileReadinessCheckType% |,0);
my $FileReadinessCheckDelay = ModifyInputValue(q|%Inputs.Source.FileReadinessCheckDelay% |,0);
my $DeliveryMode = ModifyInputValue(q|%Inputs.Options.certified_delivery% |,0);
my $RemoveTargetFiles = ModifyInputValue(q|%Inputs.Target.RemoveTargetFiles% |,0);
my $RemoveTargetDirs = ModifyInputValue(q|%Inputs.Target.removeExpiredDirsonTarget% |,0);
my $RemoveOlderThan = ModifyInputValue(q|%Inputs.Target.RemoveOlderThan% |,0);
my $TransferFilesInSortedOrder = ModifyInputValue(q|%Inputs.Options.transferFilesInSortedOrder% |,0);
my $TargetSubDir="";
my $RunFindFiles = 0;

my $SortOrder         = ModifyInputValue( q|%Inputs.Options.sortOrder% |, 0 );
my $SortCaseSensitive = ModifyInputValue( q|%Inputs.Options.sortCaseSensitive% |, 0 );
my $FileTypesRequired = ModifyInputValue( q|%Inputs.Options.fileTypesRequired% |, 0 );
my $FileTypesFirst    = ModifyInputValue( q|%Inputs.Options.fileTypesFirst% |, 0 );
my $FileTypesLast     = ModifyInputValue( q|%Inputs.Options.fileTypesLast% |, 0 );

my $OrderOptions;

my $StorageServer;
my $AccountName;
my $Bucket;
my $Container;
my $SubFolder;
my $AccessStyle;
my $AccessKey;
my $SecretKey;
my $Region;
my $EnableCheckpoint;
my $UseSSL;
my $VerifyHostname;
my $AllowSelfSignedCert;

my $FindFilesArgs = "";
my $ExplicitFiles = "";	
my $SourceData;
my @SourcePathArray;
my @SourceDirsArray;
my @IncludeFilesArray;
my @ExcludeFilesArray;
my @ExcludeSubDirListArray;

my $ManifestInputMode=$FALSE;
my $ManifestInputFile="";
my $XMLPathListMode=$FALSE;
my $XMLPathListType="";
my $SigListXattrs="";

my $TransferManifest;
my $FindFilesOptionsFile;

my $EnableFileListOutputs;

my $RET = 0;
my $WARN = 0;

sub Initialize()
{
	#
	# Export all variables now that they have been initialized and cleaned-up...
	#
	print "%dds_set%JobDateTimeStamp=$JobDateTimeStamp\n";
	print "%dds_set%SourceHost=$SourceHost\n";
	print "%dds_set%IncludeFiles=$IncludeFiles\n";
	print "%dds_set%ExcludeFiles=$ExcludeFiles\n";
	print "%dds_set%ExcludeSubdirectories=$ExcludeSubdirectories\n";
	print "%dds_set%ExcludeSubDirList=$ExcludeSubDirList\n";
	print "%dds_set%TargetHosts=$TargetHosts\n";
	print "%dds_set%TargetObjectStorage=$TargetObjectStorage\n";
	print "%dds_set%PathMappingOptions=$PathMappingOptions\n";
	print "%dds_set%MaximumJobDateTimeDirectories=$MaximumJobDateTimeDirectories\n";
	print "%dds_set%RemoveParentFolder=$RemoveParentFolder\n";
	print "%dds_set%SourceDeletionsPostTransfer=$SourceDeletionsPostTransfer\n";
	print "%dds_set%SourceDeletionExcludeDirectories=$SourceDeletionExcludeDirectories\n";
	print "%dds_set%MaximumBandwidthLimit=$MaximumBandwidthLimit\n";
	print "%dds_set%WANAcceleratorOn=$WANAcceleratorOn\n";
	print "%dds_set%WANAcceleratorAggressiveness=$WANAcceleratorAggressiveness\n";
	print "%dds_set%BandwidthCeiling=$BandwidthCeiling\n";
	print "%dds_set%BandwidthFloor=$BandwidthFloor\n";
	print "%dds_set%CompressFiles=$CompressFiles\n";
	print "%dds_set%FollowSymbolicLinks=$FollowSymbolicLinks\n";

	#
	# Process the Target Object Storage JSON
	#
	if($TargetObjectStorage -> {'s3-object-storage-access'} ne "")
	{
		$StorageServer		= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'storage-server'};
		$Bucket				= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'bucket'};
		$SubFolder			= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'subfolder'};
		$AccessStyle		= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'access-style'};
		$AccessKey			= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'access-key'};
		$SecretKey			= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'secret-key'};

		# Advanced properties

		$Region				= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'region'};
		$EnableCheckpoint	= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'enable-checkpoint'};
		$UseSSL				= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'ssl-options'} -> {'use-ssl'};
		$VerifyHostname		= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'ssl-options'} -> {'verify-hostname'};
		$AllowSelfSignedCert= $TargetObjectStorage -> {'s3-object-storage-access'} -> {'ssl-options'} -> {'allow-selfsigned-cert'};

		if($EnableCheckpoint eq 0){$EnableCheckpoint="false"}elsif($EnableCheckpoint eq 1){$EnableCheckpoint="true"}else{$EnableCheckpoint="true"}
		if($UseSSL eq 0){$UseSSL="false"}elsif($UseSSL eq 1){$UseSSL="true"}else{$UseSSL="true"}
		if($VerifyHostname eq 0){$VerifyHostname="false"}elsif($VerifyHostname eq 1){$VerifyHostname="true"}else{$VerifyHostname ="true"}
		if($AllowSelfSignedCert eq 0){$AllowSelfSignedCert="false"}elsif($AllowSelfSignedCert eq 1){$AllowSelfSignedCert="true"}else{$AllowSelfSignedCert ="false"}

		print "%dds_set%StorageServer=$StorageServer\n";
		print "%dds_set%Bucket=$Bucket\n";
		print "%dds_set%SubFolder=$SubFolder\n";
		print "%dds_set%AccessStyle=$AccessStyle\n";
		print "%dds_set%AccessKey=$AccessKey\n";
		print "%dds_set%SecretKey=$SecretKey\n";
		print "%dds_set%Region=$Region\n";
		print "%dds_set%EnableCheckpoint=$EnableCheckpoint\n";
		print "%dds_set%UseSSL=$UseSSL\n";
		print "%dds_set%VerifyHostname=$VerifyHostname\n";
		print "%dds_set%AllowSelfSignedCert=$AllowSelfSignedCert\n";
	}
	elsif($TargetObjectStorage -> {'aws-s3-storage-access'} ne "")
	{
		$Bucket        = $TargetObjectStorage -> {'aws-s3-storage-access'} -> {'bucket'};
		$SubFolder     = $TargetObjectStorage -> {'aws-s3-storage-access'} -> {'subfolder'};
		$AccessKey     = $TargetObjectStorage -> {'aws-s3-storage-access'} -> {'access-key'};
		$SecretKey     = $TargetObjectStorage -> {'aws-s3-storage-access'} -> {'secret-key'};

		print "%dds_set%Bucket=$Bucket\n";
		print "%dds_set%SubFolder=$SubFolder\n";
		print "%dds_set%AccessKey=$AccessKey\n";
		print "%dds_set%SecretKey=$SecretKey\n";
	}
	elsif($TargetObjectStorage -> {'azure-object-storage-access'} ne "")
	{
		$AccountName   = $TargetObjectStorage -> {'azure-object-storage-access'} -> {'account-name'};
		$Container     = $TargetObjectStorage -> {'azure-object-storage-access'} -> {'container'};
		$SubFolder     = $TargetObjectStorage -> {'azure-object-storage-access'} -> {'subfolder'};
		$AccessKey     = $TargetObjectStorage -> {'azure-object-storage-access'} -> {'access-key'};

		print "%dds_set%AccountName=$AccountName\n";
		print "%dds_set%Container=$Container\n";
		print "%dds_set%SubFolder=$SubFolder\n";
		print "%dds_set%AccessKey=$AccessKey\n";
	}

	#
	# Get the SourceData list and clean it up...
	#
	$SourceData = ModifyInputValue(q|%Inputs.Source.SourceData% |,0);

	if (IsSigListXmlFormat($SourceData))
	{
		# SourceData is in SigList XML format...
		my @ElArray;

		if (SigListXMLParse($SourceData,\$XMLPathListType,\@ElArray,\$SigListXattrs ) != 0)
		{
			print STDERR "ERROR: SourceData XML specification is not parsable\n";
			$RET=1;
			return;
		}
		else
		{
			@SourcePathArray = SigListGetELementsByAttribute(\@ElArray,"V");
		}

		if (uc($XMLPathListType) eq "PATHLIST")
		{
			$XMLPathListMode=$TRUE;		
		}
	}
	else
	{
		# SourceData is in legacy format...
		@SourcePathArray = SolutionSplitPaths($SourceData,',');
	}

	#
	# Clean-up each path individually and double the leading backslash for UNC paths if present...
	#
	for (my $i=0; $i < @SourcePathArray; $i++)
	{
		$SourcePathArray[$i] = ModifyInputValue($SourcePathArray[$i],0);
	}

	# 
	# If the SourceData contains a single path ending in "._manifest_" then we will use manifest mode. Manifest
	# mode will simply mean outputting each pathname in the manifest file and will not require a findfiles
	# command at all.
	# 	
	if (scalar @SourcePathArray == 1)
	{
		if (lc($SourcePathArray[0]) =~ m/\._manifest_$/)
		{
			$ManifestInputMode=$TRUE;
			$ManifestInputFile=$SourcePathArray[0];
		}
	}

	print "%dds_set%SourceData=$SourceData\n";

	print "%dds_set%XMLPathListMode=$XMLPathListMode\n";
	print "%dds_set%XMLPathListType=$XMLPathListType\n";

	print "%dds_set%ManifestInputMode=$ManifestInputMode\n";
	print "%dds_set%ManifestInputFile=$ManifestInputFile\n";

	print "%dds_set%RemoveTargetFiles=$RemoveTargetFiles\n";
	print "%dds_set%RemoveTargetDirs=$RemoveTargetDirs\n";
	print "%dds_set%RemoveOlderThan=$RemoveOlderThan\n";

	if (lc($PathMappingOptions) =~ m/source host/)
	{
		$TargetSubDir = "$TargetSubDir/$SourceAgent";
	}

	if (lc($PathMappingOptions) =~ m/job date/)
	{
		$TargetSubDir = "$TargetSubDir/$JobDateTimeStamp";
	}

	if (lc($PathMappingOptions) =~ m/job user/)
	{
		$SourceAgentUserid =~ s/(\\|\/)/_/; # replace slash in domain\user
		$TargetSubDir = "$TargetSubDir/$SourceAgentUserid";
	}

	print "%dds_set%TargetSubDir=$TargetSubDir\n";

	my $manifestSuffix = q|%manifestSuffix%|;
	$TransferManifest = 'transfer_manifest_' . $manifestSuffix;

	print STDOUT "%dds_set%manifestSuffix=$manifestSuffix\n";
	print STDOUT "%dds_set%TransferManifest=$TransferManifest\n";

	$FindFilesOptionsFile = "findfiles_options_" . $manifestSuffix;

	#
	# Disable FileList output if this isn't a one-to-one transfer, since it can't be set reliably for one-to-many transfers...
	#
	$EnableFileListOutputs = TRUE;

	my @targetArray = split / /, $TargetHosts;

	if (scalar @targetArray > 1)
	{
		$EnableFileListOutputs = FALSE;
	}

	print STDOUT "%dds_set%EnableFileListOutputs=$EnableFileListOutputs\n";

}

sub DumpVars()
{
	my $SourceDataDisplay = $SourcePathArray[0]; 

	for (my $i=1; $i < scalar @SourcePathArray; $i++)
	{
		$SourceDataDisplay = $SourceDataDisplay . "," . $SourcePathArray[$i]; 
	}

	if (length($SourceDataDisplay) > 2048)
	{
		$SourceDataDisplay=substr($SourceDataDisplay,0,2048) . "...";
	}

	print STDOUT "SourceAgent: [$SourceAgent]\n";
	print STDOUT "SourceAgentVersion: [$SourceAgentVersion]\n";
	print STDOUT "SourceAgentBuildNumber: [$SourceAgentBuildNumber]\n";
	print STDOUT "SourceAgentPlatform: [$SourceAgentPlatform]\n";
	print STDOUT "SourceAgentUserid: [$SourceAgentUserid]\n";
	print STDOUT "SourceHost: [$SourceHost]\n";
	print STDOUT "SourceData: [$SourceDataDisplay]\n";
	print STDOUT "IncludeFiles: [$IncludeFiles]\n";
	print STDOUT "ExcludeFiles: [$ExcludeFiles]\n";
	print STDOUT "ExcludeSubdirectories: [$ExcludeSubdirectories]\n";
	print STDOUT "ExcludeSubDirList: [$ExcludeSubDirList]\n";
	print STDOUT "TargetAgent: [$TargetHosts]\n";

	if($TargetObjectStorage -> {'s3-object-storage-access'} ne "")
	{
		print STDOUT "StorageEndpoint: [$StorageServer]\n";
		print STDOUT "Bucket: [$Bucket]\n";
		print STDOUT "SubFolder: [$SubFolder]\n"; 
		print STDOUT "AccessStyle: [$AccessStyle]\n";
		print STDOUT "AccessKey: [$AccessKey]\n";
		print STDOUT "SecretKey: [********]\n";
		print STDOUT "EndpointQualifier: [$Region]\n";
		print STDOUT "EnableCheckpoint: [$EnableCheckpoint]\n";
		print STDOUT "UseSSL: [$UseSSL]\n";
		print STDOUT "VerifyHostname: [$VerifyHostname]\n";
		print STDOUT "AllowSelfSignedCert: [$AllowSelfSignedCert]\n";
	}
	elsif($TargetObjectStorage -> {'aws-s3-storage-access'} ne "")
	{
		print STDOUT "Bucket: [$Bucket]\n";
		print STDOUT "SubFolder: [$SubFolder]\n"; 
		print STDOUT "AccessKey: [$AccessKey]\n";
		print STDOUT "SecretKey: [********]\n";
	}
	elsif($TargetObjectStorage -> {'azure-object-storage-access'} ne "")
	{
		print STDOUT "AccountName: [$AccountName]\n";
		print STDOUT "Container: [$Container]\n"; 
		print STDOUT "SubFolder: [$SubFolder]\n";
		print STDOUT "AccessKey: [********]\n";
	}

	print STDOUT "SkipParentFolder: [$RemoveParentFolder]\n";
	print STDOUT "SourceDeletionsPostTransfer: [$SourceDeletionsPostTransfer]\n";
	print STDOUT "SourceDeletionExcludeDirectories: [$SourceDeletionExcludeDirectories]\n";
	print STDOUT "JobLogDetail: [$LOGLEVELNAME]\n";
	print STDOUT "MaximumBandwidthLimit: [$MaximumBandwidthLimit]\n";
	print STDOUT "WANAcceleratorOn: [$WANAcceleratorOn]\n";
	print STDOUT "WANAcceleratorAggressiveness: [$WANAcceleratorAggressiveness]\n";
	print STDOUT "BandwidthCeiling: [$BandwidthCeiling]\n";
	print STDOUT "BandwidthFloor: [$BandwidthFloor]\n";
	print STDOUT "FileReadinessCheckType: [$FileReadinessCheckType]\n";
	print STDOUT "FileReadinessCheckDelay: [$FileReadinessCheckDelay]\n";

	if (lc($DeliveryMode) eq "fast") {
		print STDOUT "DeliveryMode: [Fast]\n";
	}
	elsif (lc($DeliveryMode) eq "no") {
		print STDOUT "DeliveryMode: [Normal]\n";
	}
	elsif (lc($DeliveryMode) eq "unsigned") {
		print STDOUT "DeliveryMode: [Log File Name]\n";
	}
	elsif (lc($DeliveryMode) eq "yes") {
		print STDOUT "DeliveryMode: [Certify File Delivery]\n";
	}

	SigPrintLog("Sort Order: [$SortOrder]", LOGINFO);
        SigPrintLog("Sort Case Sensitive: [$SortCaseSensitive]", LOGINFO);
        SigPrintLog("File Types Required: [$FileTypesRequired]", LOGINFO);
        SigPrintLog("File Types First: [$FileTypesFirst]", LOGINFO);
        SigPrintLog("File Types Last: [$FileTypesLast]", LOGINFO);
}

sub BuildFindFilesArgs()
{
	$RunFindFiles = 0;

	$IncludeFiles =~ s/\\/\//g;
	$ExcludeFiles =~ s/\\/\//g;
	$ExcludeSubDirList =~ s/\\/\//g;

	$ExplicitFiles = "";	

	$FindFilesArgs = "-- -optfile $FindFilesOptionsFile";

	if ($DEBUG) { print ("Building findfiles options...\n"); }

	for (my $i=0; $i < @SourcePathArray ; $i++)
	{
		if (-f $SourcePathArray[$i])
		{
			if ($ExplicitFiles eq "")
			{
				$ExplicitFiles = $SourcePathArray[$i];
			}
			else
			{
				$ExplicitFiles = $ExplicitFiles . "," . $SourcePathArray[$i];
			}
		}
		elsif (-d $SourcePathArray[$i])
		{
			push @SourceDirsArray, $SourcePathArray[$i];

			#
			# If we have at least one directory path, we will need to run findfiles...
			#
			$RunFindFiles = 1;
		}
		else
		{
			printf STDERR ("WARNING: Source file or directory not found or not accessible: $SourcePathArray[$i]\n");
			$WARN=1;
		}
	}

	if ($IncludeFiles ne "")
	{
		@IncludeFilesArray = split /,/, $IncludeFiles;

		for (my $i=0; $i < @IncludeFilesArray ; $i++)
		{
			# Remove leading & trailing whitespace...
			$IncludeFilesArray[$i] =~ s/^\s*(.*?)\s*$/$1/;
		}
	}

	if ($ExcludeFiles ne "")
	{
		@ExcludeFilesArray = split /,/, $ExcludeFiles;

		for (my $i=0; $i < @ExcludeFilesArray ; $i++)
		{
			# Remove leading & trailing whitespace...
			$ExcludeFilesArray[$i] =~ s/^\s*(.*?)\s*$/$1/;
		}
	}

	if (lc($ExcludeSubdirectories) ne "none")
	{
		if (lc($ExcludeSubdirectories) eq "all")
		{
			push @ExcludeSubDirListArray, "*";
		}
		else # specified subdirs
		{
			if ($ExcludeSubDirList ne "")
			{
				@ExcludeSubDirListArray = split /,/, $ExcludeSubDirList;

				for (my $i=0; $i < @ExcludeSubDirListArray ; $i++)
				{
					# Remove leading & trailing whitespace...
					$ExcludeSubDirListArray[$i] =~ s/^\s*(.*?)\s*$/$1/;
				}
			}
		}
	}

	if ($RunFindFiles)
	{
		if (!open(FINDFILESOPTIONSFILE,'>',$FindFilesOptionsFile))
		{
			SigPrintLog("ERROR: Could not create findfiles options file: $FindFilesOptionsFile",LOGERROR);
			$WARN=1;
			$RET=1;
		}
		else
		{
			if ($DEBUG) 
			{ 
				print ("Findfiles command: findfiles -- $FindFilesArgs\n"); 
				print ("Findfiles options:\n"); 
				print ("    -parentdirs\n"); # Will cause empty folders to be ignored
			}

			printf FINDFILESOPTIONSFILE ("-parentdirs\n");

			if (lc($FollowSymbolicLinks) eq "yes")
			{
				printf FINDFILESOPTIONSFILE ("-follow\n");

				if ($DEBUG) 
				{ 
					print "    -follow\n";
				}
			}

			for (my $i=0; $i < @SourceDirsArray; $i++)
			{
				printf FINDFILESOPTIONSFILE ("-path \"$SourceDirsArray[$i]\"\n");

				if ($DEBUG) 
				{ 
					print "    -path \"$SourceDirsArray[$i]\"\n";
				}
			}

			for (my $i=0; $i < @IncludeFilesArray; $i++)
			{
				printf FINDFILESOPTIONSFILE ("-name \"$IncludeFilesArray[$i]\"\n");

				if ($DEBUG) 
				{ 
					print "    -name \"$IncludeFilesArray[$i]\"\n";
				}
			}

			for (my $i=0; $i < @ExcludeFilesArray; $i++)
			{
				printf FINDFILESOPTIONSFILE ("-exclude \"$ExcludeFilesArray[$i]\"\n");

				if ($DEBUG) 
				{ 
					print "    -exclude \"$ExcludeFilesArray[$i]\"\n";
				}
			}

			for (my $i=0; $i < @ExcludeSubDirListArray; $i++)
			{
				printf FINDFILESOPTIONSFILE ("-direxclude \"$ExcludeSubDirListArray[$i]\"\n");

				if ($DEBUG) 
				{ 
					print "    -direxclude \"$ExcludeSubDirListArray[$i]\"\n";
				}
			}

			close(FINDFILESOPTIONSFILE);
		}
	}
	else
	{
		print ("Findfiles command: [not required - no directory paths specified]\n"); 
	}

	if ($ExplicitFiles ne "")
	{
		if ($DEBUG) 
		{ 
			print ("Additional files: $ExplicitFiles\n");
		}
	}

	if (($RunFindFiles eq 0) && ($ExplicitFiles eq ""))
	{
		SigPrintLog("ERROR: No source files or directories found for transfer.",LOGERROR);
		$WARN=1;
		$RET=1;
	}	
}

sub staticFiles
{
	# fileList  - array of full file paths

	my (@fileList) = @_;

	my %tracks = ();
	my %staticFilesTrack = ();
	my @staticFiles;

	my $FileTestMode;

	if (lc($FileReadinessCheckType) =~ /^(none|stream growing files)$/)
	{
		return;		# skip source file change tracking
	}

	if (lc($FileReadinessCheckType) eq "md5 hash")
	{
		$FileTestMode = "MD5";
		print "Generating file hashes for source file set (phase 1)...\n";
	}
	elsif (lc($FileReadinessCheckType) eq "consecutive seek")
	{
		$FileTestMode = "CSC";
		print "Generating consecutive seek info for source file set (phase 1)...\n";
	}
	else
	{
		$FileTestMode = "SIZETIME";
		print "Gathering date/time stamp info for source file set (phase 1)...\n";
	}

	#
	# Determine the status of the file at this moment in time...
	#
	foreach my $file (@fileList)
	{
		$tracks{$file} = buildFileChangeTracker($FileTestMode, $file, "");

		if ($SUPERDEBUG == 1) 
		{ 
			print "File: $file [Track: $tracks{$file}]\n"; 
		}
	}

	# sleep for X seconds
	sleep(abs($FileReadinessCheckDelay));

	#
	# Now go through each file and check it again...
	#
	if (lc($FileReadinessCheckType) eq "md5 hash")
	{
		print "Generating file hashes for source file set (phase 2)...\n";
	}
	elsif (lc($FileReadinessCheckType) eq "consecutive seek")
	{
		print "Generating consecutive seek info for source file set (phase 2)...\n";
	}
	else
	{
		print "Gathering date/time stamp info for source file set (phase 2)...\n";
	}

	foreach my $file (@fileList)
	{
		if ($staticFilesTrack{$file} ne "y")
		{
			my $track = buildFileChangeTracker($FileTestMode, $file, $tracks{$file});

			if ($SUPERDEBUG == 1) 
			{
				print "File: $file [Track: $track]\n";
			}

			if ($tracks{$file} eq $track)
			{
				if ($SUPERDEBUG == 1) 
				{ 
					print "FILE IS STATIC\n";
				}

				$staticFilesTrack{$file} = "y";
				push @staticFiles, $file;
			}
			else
			{
				print "INFO: File is not static and will be skipped: $file";
			}

			$tracks{$file} = $track;
		}   
	}   

	return @staticFiles;
}

sub buildFileChangeTracker
{
	# mode      - one of SIZE, SIZETIME, MD2, MD5, SHA1, CSC
	# file      - the file
	# hashFail  - hash value to use if the dds_hash command fails

	my ($mode, $file, $lastHash) = @_;

	my $track = "";

	$file =~ s/[\n\r]//g; # Remove newlines and/or line-feeds

	#
	# If the last hash returned _SKIP_ no need to test again...
	#
	if ($lastHash =~ m/SKIP/)
	{
		return("_SKIP_2_");
	}

	#
	# If this is a directory, there is no need to test it...
	#
	if (-d $file)
	{
		return("_DIR_OK_");
	}

	if($mode eq "SIZETIME")
	{
		my($dev,$ino,$mode,$nlink,$uid,$gid,$rdev,$size,$atime,$mtime,$ctime,$blksize,$blocks) = stat($file);
		$track = $size . ":" . $mtime . ":" . $atime;
	}
	elsif($mode eq "SIZE")
	{
		my($dev,$ino,$mode,$nlink,$uid,$gid,$rdev,$size,$atime,$mtime,$ctime,$blksize,$blocks) = stat($file);
		$track = $size;
	}
	elsif($mode eq "CSC")
	{
		if ( open FILE,"$file" ) 
		{
			#seek 0 bytes from the end, get offset, save this value
			seek FILE,0,2;
			my $offset = tell(FILE);
			$track = $offset;
			close FILE;
		}
		else 
		{
			# Open failed, file is most likely in use.  Skip it.
			if ( $SUPERDEBUG==1 ) 
			{
				print "Unable to open file $file for reading:  $!\n";
			}
			$track = "_SKIP_1_";
		}

	}
	else
	{
		#
		# There are issues with executing commands containing quote characters (in arguments) within backticks, 
		# so we have to handle those cases...
		#
		my $QUOTE;

		if (($file =~ m/\'/) && ($file =~ m/\"/)) # Filename contains both single and double quotes
		{
			$track = "_NO_TRACK_"; # We won't even try dds_hash since it will fail.
		} 
		elsif (($file =~ m/\"/) && ($IsWindows)) # Single quote won't work on Windows so we can't quote a double with a single
		{
			$track = "_NO_TRACK_"; # We won't even try dds_hash since it will fail.
		}
		else
		{
			if ($file =~ m/'/) # contains ' but no "...so " is the safe quote
			{
				$QUOTE = "\""
			}
			else # neither " nor '
			{
				if ($IsWindows)
				{
					$QUOTE = "\"" # Default for Windows
				}			
				else
				{
					$QUOTE = "'" # Default for non-Windows
				}
			}

			my $hashCmd = "dds_hash -hash $mode $QUOTE$file$QUOTE";

			$track = `$hashCmd`;

			if ($? != 0)
			{ # if hash failed, it's probably because the file's in use
				$track = "_SKIP_1_";
			}
		}
	}

	$track =~ s/[\n\r]//g; # Remove newlines and/or line-feeds

	return($track);
}

sub BuildTransferManifest()
{
	my $rc = 0;
	my $FileCount;

	my $MaxReadinessCheckFiles;

	unlink($TransferManifest);

	if ($XMLPathListMode)
	{
		if (open(TRANSFERMANIFEST,"> " . "$TransferManifest"))
		{
			for (my $i=0; $i < @SourcePathArray ; $i++)
			{
				printf TRANSFERMANIFEST ("%s\n",$SourcePathArray[$i]);
			}
		}
		else
		{
			SigPrintLog("ERROR: Could not open transfer manifest file for writing: $TransferManifest",LOGERROR);
			$RET=1;
			return;
		}

		close(TRANSFERMANIFEST);
	}
	elsif ($ManifestInputMode)
	{
		if (! -f $ManifestInputFile)
		{
			SigPrintLog("ERROR: Transfer manifest input file not found: $ManifestInputFile",LOGERROR);
			$RET=1;
			return;
		}

		if (!open(TRANSFERMANIFESTINPUT,$ManifestInputFile))
		{
			SigPrintLog("ERROR: Transfer manifest input file could not be opened: $ManifestInputFile",LOGERROR);
			$RET=1;
			return;
		}

		if (!open(TRANSFERMANIFESTOUTPUT,"> " . "$TransferManifest"))
		{
			SigPrintLog("ERROR: Transfer manifest output file could not be opened: $TransferManifest",LOGERROR);
			$RET=1;
			return;
		}		

		while (!eof(TRANSFERMANIFESTINPUT))
		{
			my $InputLine = <TRANSFERMANIFESTINPUT>;

			$InputLine =~ s/[\n\r]//g;	# Remove newlines and/or line-feeds

			my @FieldArray = split /\t/, $InputLine;  # Manifest is tab-delimited

			print TRANSFERMANIFESTOUTPUT "$FieldArray[0]\n"; # File path must always be the first field
		}

		close(TRANSFERMANIFESTINPUT);	
		close(TRANSFERMANIFESTOUTPUT);	
	}
	else # Regular findfiles specification...
	{
		#
		# Specify the individual files first...
		#
		if ($ExplicitFiles ne "")
		{
			if (open(TRANSFERMANIFEST,"> " . "$TransferManifest"))
			{
				my @FileArray = split /,/, $ExplicitFiles;
				my $FileCount = scalar @FileArray;

				for (my $i=0; $i < $FileCount; $i++)
				{
					printf TRANSFERMANIFEST ("%s\n",$FileArray[$i]);
				}
			}
			else
			{
				SigPrintLog("ERROR: Could not open transfer manifest file for writing: $TransferManifest",LOGERROR);
				$RET=1;
				return;
			}

			close(TRANSFERMANIFEST);
		}

		#
		# Now run findfiles if needed...
		#
		if ($RunFindFiles)
		{
			$RET=1;		### assume the worst

			if (!open(FFMAN, ">> $TransferManifest"))
			{
				$rc = $!;
				print STDERR "ERROR: Could not open transfer manifest file '$TransferManifest' for writing (errno '$rc')\n";
				return;
			}

			if (!open(FF, "findfiles -- $FindFilesArgs |"))
			{
				$rc = $!;
				print STDERR "ERROR: Could not run requisite 'findfiles' command to generate transfer manifest (errno '$rc')\n";
				close FFMAN;
				return;
			}

			while (<FF>)
			{
				chomp();
				print FFMAN "$_\n";
			}

			close FF;
			close FFMAN;

			if (($rc = $?) != 0)
			{
				if ((my $ffkillsig = ($rc & 0x7f)) != 0)
				{
					print STDERR "ERROR: The 'findfiles' command was killed by signal '$ffkillsig'\n";
				}
				elsif ((my $ffexitcode = ($rc >> 8)) != 0)
				{
					print STDERR "ERROR: The 'findfiles' command returned non-zero exit code '$ffexitcode'\n";
				}
				else
				{
					print STDERR "ERROR: The 'findfiles' command failed for an unknown reason\n";
				}

				return;
			}

			$RET=0;		### all is well
		}
	}

	if (! -f $TransferManifest)
	{
		SigPrintLog("ERROR: Transfer manifest file not found: $TransferManifest",LOGERROR);
		$RET=1;
		return;
	}
	else
	{
		if (open(TRANSFERMANIFEST, "< $TransferManifest"))
		{
			#
			# This cryptic bit of code actually counts the number of lines in the file...
			#
			$FileCount += tr/\n/\n/ while sysread(TRANSFERMANIFEST, $_, 2 ** 16);

			close(TRANSFERMANIFEST);
		}
	}

	#
	# Now determine the list of static files if required...
	#
	if (lc($FileReadinessCheckType) !~ /^(none|stream growing files)$/)
	{
		if (lc($FileReadinessCheckType) eq "md5 hash")
		{
			$MaxReadinessCheckFiles = 1000; # Even this will take forever!!!
		}
		else
		{
			$MaxReadinessCheckFiles = 1000000; # Could probably go larger, but this has been tested.
		}

		#
		# If we have more than the max number of files for readiness check, emit a warning and return.
		# The transfer will continue, but busy files will not be detected.
		#
		if ($FileCount > $MaxReadinessCheckFiles)
		{
			printf STDERR ("WARNING: File readiness check disabled due to too many files (Found: $FileCount - Max allowed: $MaxReadinessCheckFiles)\n");
			return;
		}

		#
		# Read the manifest file into an array...
		#
		my @TransferManifestFiles;

		if (!open(TRANSFERMANIFEST,"$TransferManifest"))
		{
			SigPrintLog("ERROR: Could not open transfer manifest: $TransferManifest",LOGERROR);
			$RET=1;
			return;
		}

		@TransferManifestFiles = <TRANSFERMANIFEST>;
		close TRANSFERMANIFEST;
		chom (@TransferManifestFiles);

		my @staticFileList = staticFiles(@TransferManifestFiles);

		if (lc($TransferFilesInSortedOrder) eq "yes")
		{
			my @staticFileList = sort { lc($a) cmp lc($b) } @staticFileList;
		}

		my @OrderedManifest;
		if ($OrderOptions) {
     		  @OrderedManifest = &OrderManifest(\@staticFileList);
     		}
     		else {
     		  @OrderedManifest = @staticFileList;
     		}

		#
		# Rewrite the transfer manifest with only the names of the static files (& dirs)...
		#

		if (open(TRANSFERMANIFEST,"> " . "$TransferManifest"))
		{
			foreach my $file (@OrderedManifest)
			{
				$file =~ s/[\n\r]//g;
				printf TRANSFERMANIFEST ("%s\n",$file);
			}
		}
		else
		{
			SigPrintLog("ERROR: Could not open transfer manifest file for writing: $TransferManifest",LOGERROR);
			$RET=1;
			return;
		}

		close(TRANSFERMANIFEST);
	}
	else
	{
		if (lc($FileReadinessCheckType) eq "stream growing files")
		{
			enableStreamGrowingFiles($FileReadinessCheckDelay);
		}

		if ((lc($TransferFilesInSortedOrder) eq "yes") || ($OrderOptions))
		{
			my @TransferManifestFiles;

			if (!open(TRANSFERMANIFEST,"$TransferManifest"))
			{
				SigPrintLog("ERROR: Could not open transfer manifest: $TransferManifest",LOGERROR);
				$RET=1;
				return;
			}

			@TransferManifestFiles = <TRANSFERMANIFEST>;
			close TRANSFERMANIFEST;
			chomp (@TransferManifestFiles);

			my @TransferManifestFiles = sort { lc($a) cmp lc($b) } @TransferManifestFiles;

			#
			# Sort manifest and check against requirements
			#
			my @OrderedManifest;
			if ($OrderOptions) {
     			  @OrderedManifest = &OrderManifest(\@TransferManifestFiles);
     			}
     			else {
     			  @OrderedManifest = @TransferManifestFiles;
     			}

			if (open(TRANSFERMANIFEST,"> " . "$TransferManifest"))
			{
				foreach my $file (@OrderedManifest)
				{
					$file =~ s/[\n\r]//g;
					printf TRANSFERMANIFEST ("%s\n",$file);
				}
			}
			else
			{
				SigPrintLog("ERROR: Could not open transfer manifest file for writing: $TransferManifest",LOGERROR);
				$RET=1;
				return;
			}

			close(TRANSFERMANIFEST);
		}
	}
}

{
	my $growingFileControlMessage;	##

	sub enableStreamGrowingFiles()
	{
		my ($inactivityPeriod) = @_;

		return if (defined($growingFileControlMessage));	# only emit this message once

		if ($inactivityPeriod !~ /\d+/) {
			$inactivityPeriod = 1;
		}

		$growingFileControlMessage = "%dds_set%growing_file_control='inactivity_period=$inactivityPeriod'";
		print STDOUT "$growingFileControlMessage\n";
	}
}


sub OrderManifest {

  my @files = @{$_[0]};
  my $file;
  my $i;
  my @FileTypes;
  my $ft;
  my @rfile;
  my $insert;
  my $fcount;
  my @lastfiles;

  SigPrintLog("Ordering manifest...",LOGINFO);
  SigPrintLog("Files: ".join(',',@files),LOGINFO);

  # Sorting
  if ($SortOrder eq 'ascending') {
    SigPrintLog("Sorting file list ascending...",LOGINFO);
    if ($SortCaseSensitive eq 'yes') {
      @files = sort {$a cmp $b} @files;
    }
    else {
      @files = sort {lc($a) cmp lc($b)} @files;
    }
    SigPrintLog("Sorted files: ".join(',',@files),LOGINFO);
  }
  elsif ($SortOrder eq 'descending') {
    SigPrintLog("Sorting file list descending...",LOGINFO);
    if ($SortCaseSensitive eq 'yes') {
      @files = sort {$b cmp $a} @files;
    }
    else {
      @files = sort {lc($b) cmp lc($a)} @files;
    }
    SigPrintLog("Sorted files: ".join(',',@files),LOGINFO);
  }
  else {
    SigPrintLog("Not sorting file list",LOGINFO);
  }

  # Required file types
  if ($FileTypesRequired ne '') {
    SigPrintLog('Checking if required file types are present...',LOGINFO);
    @FileTypes = split(/,/,$FileTypesRequired);

    foreach $file (@files) {
      foreach $i (0..$#FileTypes) {
        if ($file =~ /$FileTypes[$i]$/i) {
          SigPrintLog("Matched filetype of $FileTypes[$i] on $file",LOGINFO);
          splice(@FileTypes,$i,1);
          last;
        }
      }
      last if ($#FileTypes == -1);
    }
    if ($#FileTypes != -1) {
      SigPrintLog("File types not found: ".join(',',@FileTypes),LOGWARN);
      SigPrintLog("Preventing file transfer of any files.",LOGWARN);
      return ();
    }
  }

  # Move file types to top of manifest
  if ($FileTypesFirst ne '') {
    SigPrintLog("Moving files to top of manifest...",LOGINFO);
    $insert = 0;
    @FileTypes = split(/,/,$FileTypesFirst);
    for $i (0..$#files) {
      for $ft (@FileTypes) {
        if ($files[$i] =~ /$ft$/i) {
          SigPrintLog("Matched file type $ft; moving $files[$i]",LOGINFO);
          splice(@files,$insert,0,splice(@files,$i,1));
          $insert++;         
          last;
        }
      }
    }
    SigPrintLog("Files at top: ".join(',',@files),LOGINFO);
  }

  # Move file types to bottom of manifest
  if ($FileTypesLast ne '') {
    SigPrintLog("Moving files to bottom of manifest...",LOGINFO);
    $insert = 0;
    @FileTypes = split(/,/,$FileTypesLast);
    $fcount = $#files;
    $i = 0;
    while ($i <= $fcount) {
      for $ft (@FileTypes) {
        if ($files[$i] =~ /$ft$/i) {
          SigPrintLog("Matched file type $ft; moving $files[$i]",LOGINFO);
          push(@lastfiles,splice(@files,$i,1));
          $fcount--;
          last;
        }
      }
      $i++;
    }
    push(@files,@lastfiles);
    SigPrintLog("Files at bottom: ".join(',',@files),LOGINFO);
  } 

  return (@files);

}


sub Validate () {

  # Default case insensitive sort
   $SortCaseSensitive = tr/A-Z/a-z/;
   $SortCaseSensitive = 'yes';

   # Validate sort case sensitivity
   if (($SortCaseSensitive ne 'yes') && ($SortCaseSensitive ne 'no')) {
     SigPrintLog("Invalid sort case sensitivity of '$SortCaseSensitive'; must be one of yes or no.", LOGERROR);
     $RET = 1;
     return;
   }

   # Default of unsorted
   $SortOrder =~ tr/A-Z/a-z/;
   $SortOrder = 'unsorted' if ($SortOrder =~ /^\s*$/);

   # Validate sort order option
   if (($SortOrder ne 'unsorted') && ($SortOrder ne 'ascending') && ($SortOrder ne 'descending')) {
     SigPrintLog("Invalid sort order of '$SortOrder'; must be one of unsorted, ascending or descending.", LOGERROR);
     $RET = 1;
     return;
   }

   # Were any file name ordering options specified?
   # Don't want to do ordering if not needed for efficiency
   $OrderOptions = 0;
   $OrderOptions = 1 if ($SortOrder         ne 'unsorted');
   $OrderOptions = 1 if ($FileTypesRequired !~ /^\s*$/);
   $OrderOptions = 1 if ($FileTypesFirst    !~ /^\s*$/);
   $OrderOptions = 1 if ($FileTypesLast     !~ /^\s*$/);

   if ($OrderOptions) {
     SigPrintLog('Transfer ordering enabled',LOGINFO);
   }
   else {
     SigPrintLog('Transfer ordering disabled',LOGINFO);
   }

}


###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

DumpVars();

Validate ();

if (! $XMLPathListMode && ! $ManifestInputMode)
{
	BuildFindFilesArgs();
}

if ($RET == 0)
{
	BuildTransferManifest();
}

if ($WARN == 0)
{
	print "%dds_set%InitializationWarning=0\n";
}
else
{
	print "%dds_set%InitializationWarning=1\n";
}

if ($RET == 0)
{
	if ($DEBUG)
	{
		if ($WARN == 0)
		{
			print STDOUT ("Success: Source Initialization\n");
		}
		else
		{
			print STDOUT ("Success: Source Initialization (with warnings)\n");
		}
	}

	print "%dds_set%InitializationError=0\n";
}
else
{
	print STDERR ("Failure: Source Initialization\n");

	print "%dds_set%InitializationError=1\n";
	print "%dds_set%ErrorInJob=1\n";
}

if ($RunFindFiles)
{
	unlink($FindFilesOptionsFile);
}

exit $RET;

