#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionModifyInputValue%
%script_lib_obj:SolutionNotify%

use Cwd;

print STDOUT "Executing SigniantObjectUpload component: %job_template%\n";

#
# Initialize globals...
#

my $DdsTargetTempDir = Cwd::cwd();
print "%dds_set%DdsTargetTempDir=$DdsTargetTempDir\n";

my $TargetAgent;
my $TargetAgentVersion;
my $TargetAgentBuildNumber;
my $TargetAgentPlatform;
my $TargetAgentUserid;
my $JobDateTimeStamp;
my $SimultaneousTransferStreams;
my $manifestSuffix;
my	$TransferManifest;

my $RET = 0;

sub Initialize()
{
	$TargetAgent = ModifyInputValue(q|%tgt_agent% |,0);
	$TargetAgentVersion = ModifyInputValue(q|%agent_release_version% |,0);
	$TargetAgentBuildNumber = ModifyInputValue(q|%agent_build_identifier% |,0);
	$TargetAgentPlatform = ModifyInputValue(q|%tgt_platform% |,0);
	$TargetAgentUserid = ModifyInputValue(q|%tgt_user% |,0);
	$SimultaneousTransferStreams = ModifyInputValue(q|%Inputs.Transport.simultaneousTransferStreams% |,0);

	#
	# Set the global date/timestamp label used for log files and version directories.
	#
	my @TIMEARRAY;   

	@TIMEARRAY = gmtime(time);   

	$JobDateTimeStamp = sprintf ( "%02d-%02d-%02d_%02d-%02d-%02d.%05d", ($TIMEARRAY[5]-100),($TIMEARRAY[4]+1),$TIMEARRAY[3],$TIMEARRAY[2],$TIMEARRAY[1],$TIMEARRAY[0],$$);   
	print "%dds_set%JobDateTimeStamp=$JobDateTimeStamp\n";

	if ($SimultaneousTransferStreams eq "" || $SimultaneousTransferStreams =~ /^\%/) {
		$SimultaneousTransferStreams = 11; # Default 10 transfer streams - plus the manifest stream
	} else {
		$SimultaneousTransferStreams++; # Add one for the manifest stream
	}

	print STDOUT "%dds_set%number_of_transfer_streams=$SimultaneousTransferStreams\n";

	$manifestSuffix = '%job_id%' . '-' . $$ . '-' . time() . '.txt';
	print STDOUT "%dds_set%manifestSuffix=$manifestSuffix\n";

	$TransferManifest='transfer_manifest_' . $manifestSuffix;
	print STDOUT "%dds_set%TransferManifest=$TransferManifest\n";

	$RET = 0;
}

sub DumpVars()
{
	print STDOUT "TargetAgent: [$TargetAgent]\n";
	print STDOUT "TargetAgentVersion: [$TargetAgentVersion]\n";
	print STDOUT "TargetAgentBuildNumber: [$TargetAgentBuildNumber]\n";
	print STDOUT "TargetAgentPlatform: [$TargetAgentPlatform]\n";
	print STDOUT "TargetAgentUserid: [$TargetAgentUserid]\n";
	print STDOUT "JobDateTimeStamp: [$JobDateTimeStamp]\n";
	print STDOUT "SimultaneousTransferStreams: [" . ($SimultaneousTransferStreams - 1) . "]\n";
}

###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

DumpVars();

if ($RET == 0)
{
	if ($DEBUG)
	{
		print STDOUT ("Success: Target Initialization\n");
	}
}
else
{
	print STDERR ("Failure: Target Initialization\n");
	print "%dds_set%ErrorInJob=1\n";
}

exit $RET;

