#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%

#
# The manifest file used here has been generated in the src_setup_cmd. It contains the paths of all files matching the filter
# criteria and which have optionally passed the File Readiness Check.
#
# Manifest file format:
#
#	Field 1: FileName (including path)
#
my $TransferManifest  = q|%TransferManifest%|;

if (! -f $TransferManifest  )
{
	SigPrintLog("ERROR: Transfer manifest file not found: $TransferManifest",LOGERROR);
	exit(1);
}

if (!open(TRANSFERMANIFEST,$TransferManifest  ))
{
	SigPrintLog("ERROR: Transfer manifest file could not be opened: $TransferManifest",LOGERROR);
	exit(1);
}

while (!eof(TRANSFERMANIFEST))
{
	my $InputLine = <TRANSFERMANIFEST>;

	$InputLine =~ s/[\n\r]//g;	# Remove newlines and/or line-feeds

	my @FieldArray = split /\t/, $InputLine;  # Manifest is tab-delimited

	print STDOUT "$FieldArray[0]\n";
}

close(TRANSFERMANIFEST);

exit(0);

