#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionSigListXML%
%script_lib_obj:SolutionModifyInputValue%
%script_lib_obj:SolutionSplitPaths%

use File::Basename;

my $SUPERDEBUG = 0;

my $FILENAME;
my $FILEHOST;
my $FILESIZE;
my $FILETYPE;
my $FILEDATE;
my $FILETIME;

my $SourceAgent = ModifyInputValue(q|%src_agent%|,0);
my $JobDateTimeStamp = ModifyInputValue(q|%JobDateTimeStamp%|,0);
my $SourceAgentUserid = ModifyInputValue(q|%src_user%|,0);
$SourceAgentUserid =~ s|[\\/]|_|g;	## replace any '/' or '\' with '_'
my $RemoveParentFolder = q|%RemoveParentFolder%|;
my $PathMappingOptions = q|%PathMappingOptions%|;
my $VerifyForSufficientDiskSpace = ModifyInputValue(q|%Inputs.Options.verifyforsufficientdiskspacebeforeJobtransfersstart% |,0);

my %SearchPathHash = ();

my $XMLPathListMode = $FALSE;
my $FlattenTargetPaths = $FALSE;

use constant SKIPFILE_RESPONSE => '*** skip ***';

##################
### Initialize ###
##################

sub Initialize()
{
	my $SourceData = ModifyInputValue(q|%Inputs.Source.SourceData% |,0);
	my @SourcePathArray;

	if (!IsSigListXmlFormat($SourceData))
	{
		# SourceData is in legacy format...
		@SourcePathArray = SolutionSplitPaths($SourceData,',');
	}
	else	# SourceData is in SigList XML format
	{
		my @elementArray;
		my $xmlPathListType;
		my $xmlXattrs;

		if (SigListXMLParse($SourceData, \$xmlPathListType, \@elementArray, \$xmlXattrs) != 0) {
			print STDERR "ERROR: SourceData XML specification is not parsable\n";
			exit(1);
		}

		if (uc($xmlPathListType) ne "FILEDIR" && uc($xmlPathListType) ne "PATHLIST" && uc($xmlPathListType) ne "MULTILINEVAL") {
			print STDERR "ERROR: SourceData XML specification has an unsupported pathlist type '$xmlPathListType'.\n";
			exit(1);
		}

		if ((uc($xmlPathListType) eq "FILEDIR") || (uc($xmlPathListType) eq "MULTILINEVAL"))
		{
			@SourcePathArray = SigListGetELementsByAttribute(\@elementArray, "V");
		}
		else	## PATHLIST
		{
			@SourcePathArray = SolutionSplitPaths($xmlXattrs, ',');
			$FlattenTargetPaths = (scalar(@SourcePathArray) == 0);
			$XMLPathListMode = $TRUE;
		}
	}

	foreach my $sourcePath (@SourcePathArray) {
		my $searchKey = lc(ModifyInputValue($sourcePath,0));
		$searchKey =~ s|\\|/|g;		## convert all '\' chars to '/' to simplify REs for path parsing/matching
		$SearchPathHash{$searchKey} = $searchKey;
		SigPrintLog("SearchPathHash: $SearchPathHash{$searchKey}",LOGDEBUG,LOGSTDERR) if $SUPERDEBUG;
	}
}

sub RemoveLastPathComponent($)
{
	use File::Basename;

	my $returnedPath = dirname(@_[0]);
	$returnedPath =~ s|/+$||;
	return $returnedPath;
}	

###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

while (<STDIN>)
{
	#
	# Format of expected input line (in implicit '$_' from <STDIN>) is:
	#
	#	"Filename"  Agent_Host  File_Size  File_Type  File_Date  File_Time
	#

	print STDERR ("DEBUG: Line is - $_\n") if $SUPERDEBUG;

	m/\"(.+)?\"\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)/;

	#" <-- Workaround for syntax-highlighting glitch caused by previous regex

	$FILENAME = $1;
	$FILEHOST = $2;
	$FILESIZE = $3;
	$FILETYPE = $4;
	$FILEDATE = $5;
	$FILETIME = $6;

	$FILENAME =~ s|\\|/|g;

	if ($SUPERDEBUG)
	{
		print STDERR "FILENAME: $FILENAME\n";
		print STDERR "FILEHOST: $FILEHOST\n";
		print STDERR "FILESIZE: $FILESIZE\n";
		print STDERR "FILETYPE: $FILETYPE\n";
		print STDERR "FILEDATE: $FILEDATE\n";
		print STDERR "FILETIME: $FILETIME\n";
	}

	if(lc($VerifyForSufficientDiskSpace) eq "yes"){
		my $freeSpaceLeftOnTgt = q|%Inputs.Target.targetFreeSpace%|;
		print STDERR "Initial free space on drive = [$freeSpaceLeftOnTgt]\n" if $SUPERDEBUG;

		$freeSpaceLeftOnTgt = $freeSpaceLeftOnTgt - $FILESIZE;

		print STDERR "Free space left on drive = [$freeSpaceLeftOnTgt]\n" if $SUPERDEBUG;

		if($freeSpaceLeftOnTgt < 1)
		{
			print STDERR "Not enough free space left on target agent machine to transfer files\n";
			exit(1);	
		}
	}

	if ($XMLPathListMode && $FlattenTargetPaths)
	{
		if (lc($FILETYPE) eq "file")
		{
			print STDERR "Stripping $FILENAME: " . basename($FILENAME) . "\n" if $SUPERDEBUG;
			$FILENAME = basename($FILENAME);
		}
		else
		{
			$FILENAME = SKIPFILE_RESPONSE;
		}
	}
	elsif (lc($FILETYPE) eq "file" && exists $SearchPathHash{lc($FILENAME)})
	{
		print STDERR "Stripping $FILENAME: " . basename($FILENAME) . "\n" if $SUPERDEBUG;
		$FILENAME = basename($FILENAME);
	}
	else
	{
		my $matchfound = 0;
		my $matchkey = "";

		for my $key (keys %SearchPathHash)
		{
			if (lc($FILENAME) =~ m/^\Q$key\E/)
			{
				$matchfound = 1;
				$matchkey = $key;
				last;
			}
		}

		if ($matchfound)
		{
			print STDERR "Unprefixing: $FILENAME ...\n" if $SUPERDEBUG;

			# Remove parent folder if required
			#
			if (lc($RemoveParentFolder) eq "no")
			{
				$matchkey = RemoveLastPathComponent($matchkey);
			}

			if (lc($FILENAME) eq lc($matchkey))
			{
				$FILENAME = "";
			}
			else
			{
				$FILENAME =~ s|^\Q$matchkey\E/||i;
			}

			print STDERR "New filename: $FILENAME\n" if $SUPERDEBUG;
		}
	}

	if ($FILENAME =~ m|^//|)			# UNC specification?
	{
		$FILENAME =~  s|^/*[^/]*/*[^/]*/*||;	# Remove "//host/share/" portion
	}
	elsif ($FILENAME =~ m|^([a-z]:)?/|i)	# Leading slash, with optional drive letter?
	{
		$FILENAME =~ s|^([a-z]:)?/||i;	# Remove it
	}

	if ($FILENAME eq "")
	{
		$FILENAME = SKIPFILE_RESPONSE;
	}
	else
	{
		if (lc($PathMappingOptions) =~ m/job user/)
		{
			$FILENAME = "$SourceAgentUserid/$FILENAME";
		}

		if (lc($PathMappingOptions) =~ m/job date/)
		{
			$FILENAME = "$JobDateTimeStamp/$FILENAME";
		}

		if (lc($PathMappingOptions) =~ m/source host/)
		{
			$FILENAME = "$SourceAgent/$FILENAME";
		}
	}

	print "$FILENAME\n";

	print STDERR "DEBUG: MAPPED: $FILENAME\n" if $SUPERDEBUG;
}

exit 0;

#
# Copyright 2000-2008 Signiant Corp.
#

#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%

my $ExtraLocalDebug = FALSE;

#
# Open "Prefile Inuse Manifest" if post-transfer source file deletion is selected.
#
my $ManifestFH;
my $SourceDeletionsPostTransfer = q|%SourceDeletionsPostTransfer%|;
if ($SourceDeletionsPostTransfer =~ /xfrdfiles/i)
{
	my $targetAgent = "%tgt_agent%";
	my $manifestSuffix = "%manifestSuffix%";
	my $manifestFileName = "inuse_prefile_" . $targetAgent . "_" . $manifestSuffix;

	SigPrintLog("DEBUG: Opening Inuse-Prefile manifest '$manifestFileName' for output", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	if (!open($ManifestFH, "> $manifestFileName"))
	{
	    	SigPrintLog("ERROR: Could not create Inuse-Prefile manifest '$manifestFileName' -- continuing", LOGERROR, LOGSTDERR);
		print STDERR "%dds_set%ErrorInJob=1\n";
	}
	else
	{
	    select $ManifestFH; $| = 1;
	    select STDOUT; $| = 1;
	}
}

#
# Main Continuous Prefile Loop
#

while (<STDIN>)
{
	chomp;	# drop newline

	SigPrintLog("DEBUG: Input line is ($_)", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	#
	# Format of expected input line (in implicit '$_' from <STDIN>) is:
	#
	#	"Filename" File_Disposition File_Type File_Size File_Date File_Time File_Disposition_Qualifier
	#
	#	where File_Type is one of:
	#
	#		D		(meaning directory)
	#		F		(meaning file)
	#		L		(meaning symbolic link)
	#		S		(meaning "something else")
	#
	#	and where File_Disposition is one of:
	#
	#		transferring
	#		skipping
	#		deleting
	#		error_skipping
	#		unknown_operation
	#

	my $isMatched =  m/\"(.+)?\"\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)(\s+)?(\S+)?/;	#" <-- workaround syntax-highlighting glitch
		# NOTE: With newline removed from input line, a "zero or one" match (?) is needed for
		#       subexpressions 7 and 8 in order to match lines having either 6 or 7 tokens.
	if (! $isMatched)
	{
		SigPrintLog("DEBUG: Cannot match tokens of input tuple!!", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;
		print STDOUT "ok\n";
		next;
	}

	#
	# Get tokens into the named scalars.
	#
	my $fileName = $1;
	my $fileDisp = $2;
	my $fileType = $3;
	my $fileSize = $4;
	my $fileDate = $5;
	my $fileTime = $6;
	my $fileDispQual = $8;

	if ($fileDispQual =~ /^\"(.+)\"$/)	#" <-- workaround syntax-highlighting glitch
	{
		$fileDispQual = $1;
	}

	SigPrintLog("DEBUG: Parsed input tokens are [$fileName] [$fileDisp] [$fileType] [$fileSize] [$fileDate] [$fileTime] [$fileDispQual]\n", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	if ((uc($fileDispQual) eq "INUSE") && (uc($fileType) eq "F") && defined($ManifestFH) && defined(fileno($ManifestFH)))
	{
		SigPrintLog("DEBUG: Adding '$fileName' to Inuse-Prefile manifest", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

		print $ManifestFH "$fileName\n";
	}

	print STDOUT "ok\n";
}

exit(0);

