# Copyright 2000-2011 Signiant Corp.
#

#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%

my $ExtraLocalDebug = FALSE;

#
# Open "Postfile Inuse Manifest" if post-transfer source file deletion is selected.
#
my $ManifestFH;
my $SourceDeletionsPostTransfer = q|%SourceDeletionsPostTransfer%|;
if ($SourceDeletionsPostTransfer =~ /xfrdfiles/i)
{
	my $targetAgent = "%tgt_agent%";
	my $manifestSuffix = "%manifestSuffix%";
	my $manifestFileName = "inuse_postfile_" . $targetAgent . "_" . $manifestSuffix;

	SigPrintLog("DEBUG: Opening Inuse-Postfile manifest '$manifestFileName' for output", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	if (!open($ManifestFH, "> $manifestFileName"))
	{
	    	SigPrintLog("ERROR: Could not create Inuse-Postfile manifest '$manifestFileName' -- continuing", LOGERROR, LOGSTDERR);
		print STDERR "%dds_set%ErrorInJob=1\n";
	}
	else
	{
	    select $ManifestFH; $| = 1;
	    select STDOUT; $| = 1;
	}
}

while (<STDIN>)
{
	chomp;	# drop newline

	SigPrintLog("DEBUG: Input line is ($_)", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	#
	# Format of expected input line (in implicit '$_' from <STDIN>) is:
	#
	#	"Filename" Disposition_1 Optional_Disposition_2 File_Type File_Size File_Date File_Time Disposition_Qualifier
	#
	#	where File_Type is one of:
	#
	#		D		(meaning directory)
	#		F		(meaning file)
	#		L		(meaning symbolic link)
	#		S		(meaning "something else")
	#
	#	and where Disposition_1 with Optional_Disposition_2 (what a mess!!) is one of:
	#
	#		transferred
	#		skipped
	#		deleted
	#		error
	#		skipped error	(pre-transfer error caused file to be skipped)
	#		skipped delete	(delete skipped by request from prefile cmd)
	#		what happened	(???? internal agent error)
	#

	my $isMatched =  m/\"(.+)?\"\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)(\s+)?(\S+)?(\s+)?(\S+)?/;	#" <-- workaround syntax-highlighting glitch
		# NOTE: With newline removed from input line, a "zero or one" match (?) is needed for
		#       subexpressions 7 thru 10 in order to match lines having either 6 or 7 or 8 tokens.
	if (! $isMatched)
	{
		SigPrintLog("DEBUG: Cannot match tokens of input tuple!!", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;
		print STDOUT "\n";
		next;
	}

	#
	# Get tokens into the named scalars, first creating single disposition from possibly two disposition words (yuck!)
	#
	my $fileDisp = $2;
	my $isSingleDispWord = TRUE;

	if (($fileDisp eq "skipped" or $fileDisp eq "what") and length($3) > 1)
	{
		$fileDisp = $fileDisp . "_" . $3;	# join disposition words
		$isSingleDispWord = FALSE;
	}

	my $fileName = $1;
	my $fileType = ($isSingleDispWord ? $3 : $4);
	my $fileSize = ($isSingleDispWord ? $4 : $5);
	my $fileDate = ($isSingleDispWord ? $5 : $6);
	my $fileTime = ($isSingleDispWord ? $6 : $8);
	my $fileDispQual = ($isSingleDispWord ? $8 : $10);

	if ($fileDispQual =~ /^\"(.+)\"$/)	#" <-- workaround syntax-highlighting glitch
	{
		$fileDispQual = $1;
	}

	SigPrintLog("DEBUG: Parsed input tokens are [$fileName] [$fileDisp] [$fileType] [$fileSize] [$fileDate] [$fileTime] [$fileDispQual]\n", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

	if ((uc($fileDispQual) eq "INUSE") && (uc($fileType) eq "F") && defined($ManifestFH) && defined(fileno($ManifestFH)))
	{
		SigPrintLog("DEBUG: Adding '$fileName' to Inuse-Postfile manifest", LOGDEBUG, LOGSTDERR) if $ExtraLocalDebug;

		print $ManifestFH "$fileName\n";
	}

	print STDOUT "\n";
}

exit(0);

