#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionSigListXML%
%script_lib_obj:SolutionModifyInputValue%

my $SUPERDEBUG=0;

my $FILENAME;
my $FILEDISP;
my $FILETYPE;
my $FILESIZE;
my $FILEDATE;
my $FILETIME;

my $FILESIZE_formatted;
my $DATETIME_formatted;

my $Bucket;
my $SubFolder;
my $TransferredManifest="";
my $TransferredManifestCreated=0;
my $SkippedManifest="";
my $SkippedManifestCreated=0;

my $SourceAgent = q|%src_agent%|;
my $TargetAgent = q|%tgt_agent%|;

my $EnableFileListOutputs="";

my $OneKB 	= 1024;
my $OneMB 	= 1024000;
my $OneGB 	= 1024000000;
my $DdsTargetTempDir;
my $manifestSuffix;

#
# Function:	Initialize
#
# Description:	Initialize global variables.
#
sub Initialize
{
	$Bucket = ModifyInputValue(q|%Bucket% |,0);
	if($Bucket eq "\%Bucket\%"){
	  $Bucket = ModifyInputValue(q|%Container% |,0); #Azure storage, need to get the Container value
	}
	$Bucket =~ s/\\/\//g;
	$Bucket = $Bucket . "/";

	$SubFolder = ModifyInputValue(q|%SubFolder% |,0);
	$SubFolder =~ s/\\/\//g;
	if($SubFolder ne ""){
	  $SubFolder = $SubFolder . "/";
	}

	$DdsTargetTempDir = q|%DdsTargetTempDir%|;
	$manifestSuffix = q|%manifestSuffix%|;

	$TransferredManifest = $DdsTargetTempDir . '/transferred_files.' . $manifestSuffix;
	$SkippedManifest = $DdsTargetTempDir . '/skipped_files.' . $manifestSuffix;

	$EnableFileListOutputs= q|%EnableFileListOutputs%|;
}

#
# Function:	OpenLogFiles
#
# Description:	Creates and opens the logfiles.
#
sub OpenLogFiles
{
	if (open(XFERSMANIFESTFILE,"> " . $TransferredManifest))
	{
		$TransferredManifestCreated=1;
		print STDERR "%dds_set%TransferredManifestCreated=$TransferredManifestCreated\n";

		#
		# Enable auto-flushing on opened file because the close operation following
		# the main "infinite" loop will likely never be reached.
		#
		select XFERSMANIFESTFILE; $| = 1; select STDOUT;
	}
	else
	{
		SigPrintLog("ERROR: Could not create transferred files manifest: $TransferredManifest", LOGERROR, LOGSTDERR);
	}

	if (open(SKIPPEDMANIFESTFILE,"> " . $SkippedManifest))
	{
		$SkippedManifestCreated=1;
		print STDERR "%dds_set%SkippedManifestCreated=$SkippedManifestCreated\n";

		#
		# Enable auto-flushing on opened file because the close operation following
		# the main "infinite" loop will likely never be reached.
		#
		select SKIPPEDMANIFESTFILE; $| = 1; select STDOUT;
	}
	else
	{
		SigPrintLog("ERROR: Could not create skipped files manifest: $SkippedManifest", LOGERROR, LOGSTDERR);
	}
}

###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

if ($EnableFileListOutputs)
{
	OpenLogFiles();
}

while (<STDIN>)
{
	my $ShiftArgs = 0;

	SigPrintLog("DEBUG: Line is - $_", LOGDEBUG, LOGSTDERR) if $SUPERDEBUG;

	if ( m/\"(.+)?\"\s+(\S+)?\s+(\S+)?\s+(\S+)?\s+(\S+)?\s+(\S+)?\s+(\S+)?/ )
	{	#"  <-- Quote is a workaround for syntax highlighting issue caused by previous regex
		$FILENAME = $1;

		#
		# File dispositions for post-file commands:
		# 
		# "transferred"
		# "skipped"
		# "deleted"
		# "error"
		# "skipped error"
		# "skipped delete"
		#
		$FILEDISP = $2;

		if ($3 eq "error")
		{
			$FILEDISP = "skipped error";
			$ShiftArgs = 1;
		}
		elsif ($3 eq "delete")
		{
			$FILEDISP = "skipped delete";
			$ShiftArgs = 1;
		}

		if (! $ShiftArgs)
		{
			#
			# File types:
			#
			# "D" - Directory
			# "F" - Regular file
			# "L" - Sym Link
			# "S" - Something else!
			#
			$FILETYPE = $3;

			$FILESIZE = $4;
			$FILEDATE = $5;
			$FILETIME = $6;
		}
		else
		{
			$FILETYPE = $4;
			$FILESIZE = $5;
			$FILEDATE = $6;
			$FILETIME = $7;
		}

		$FILENAME =~ s/\\/\//g;

		my $DISPLAYFILETYPE;

		SigPrintLog("Fields: [$FILENAME] [$FILEDISP] [$FILETYPE] [$FILESIZE] [$FILEDATE] [$FILETIME]", LOGDEBUG, LOGSTDERR) if $SUPERDEBUG;

		printf STDOUT ("\n"); # Agent expects a line of output - even if blank - to indicate the file has been processed.

		if (($FILEDISP eq "transferred") || ($FILEDISP eq "skipped"))
		{
			if ($FILESIZE < $OneKB)
			{
				$FILESIZE_formatted = $FILESIZE . " Bytes";
			}
			elsif ($FILESIZE < $OneMB)
			{
				my $KBYTES = $FILESIZE / 1024;
				$FILESIZE_formatted = int($KBYTES) . " KBytes";
			}
			else
			{
				my $MBYTES = $FILESIZE / 1024000;
				$FILESIZE_formatted = int($MBYTES) . " MBytes";
			}

			if ($FILETYPE eq "S")
			{
				$FILETYPE="U";
			}

			# Date/time must be in "W3C" date/time format
			$FILEDATE =~ s/\//-/g;
			$DATETIME_formatted = $FILEDATE . "T" . $FILETIME . "Z";

			my $TargetPath = $Bucket . $SubFolder . $FILENAME;

			my $XMLString = "<EL V=\'" . SigEscapeXML($TargetPath) . "\'";
			$XMLString = $XMLString . " T=\'" . $FILETYPE . "\'";
			$XMLString = $XMLString . " S=\'" . $FILESIZE . "\'";
			$XMLString = $XMLString . " MT=\'" . $DATETIME_formatted . "\' />";

			if ($FILEDISP eq "transferred")
			{
				print XFERSMANIFESTFILE "$XMLString\n" if $TransferredManifestCreated;

				SigPrintLog("Transferred: $FILENAME - Size: $FILESIZE_formatted - Date: $FILEDATE - Time: $FILETIME", LOGDEBUG, LOGSTDERR);
			}

			if ($FILEDISP eq "skipped")
			{
				print SKIPPEDMANIFESTFILE "$XMLString\n" if $SkippedManifestCreated;

				SigPrintLog("Skipped: $FILENAME - Size: $FILESIZE_formatted - Date: $FILEDATE - Time: $FILETIME", LOGDEBUG, LOGSTDERR);
			}
		}
	}
}

#
# NOTE: These close operations are basically here for semantic completeness because, unless the above "infinite" loop
# is broken accidentally, it's unlikely they'll ever be reached. This is why auto-flushing has been previously enabled.
#
close(XFERSMANIFESTFILE) if $TransferredManifestCreated;

close(SKIPPEDMANIFESTFILE) if $SkippedManifestCreated;

exit(0);

#    
# Copyright 2000-2010 Signiant Corp.    
#

#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionModifyInputValue%
%script_lib_obj:SolutionNotify%
%script_lib_obj:SolutionDebugLog%

require File::Path;
require File::Copy;

#
# Globals...
#

my $TargetDir = ModifyInputValue(q|%TargetDirectory% |,0);

my $SourceAgent = ModifyInputValue(q|%src_agent%|,0);
my $JobDateTimeStamp = ModifyInputValue(q|%JobDateTimeStamp%|,0);
my $SourceAgentUserid = ModifyInputValue(q|%src_user%|,0);
my $PathMappingOptions = q|%PathMappingOptions%|;
my $MaximumJobDateTimeDirectories = q|%MaximumJobDateTimeDirectories%|;

#
# Return the array of date/time subdirectories under the given parent directory
#
sub BuildSubDirList($)
{
	my ($PARENTDIR) = @_;
	my @DIRLIST;
	my $DIRENTRY;

	if ( opendir ( HDIR, $PARENTDIR ) )
	{
		while ( $DIRENTRY = readdir( HDIR ) )
		{
			# Only count directories
			if ( -d "$PARENTDIR/$DIRENTRY" )
			{
				# Only pick up our time-stamped directories ( format: dd-dd-dd_dd-dd-dd...)
				if ($DIRENTRY =~ m/^\d\d-\d\d-\d\d_\d\d-\d\d-\d\d/)
				{
					push ( @DIRLIST, "$DIRENTRY" );
				} 
			}
		} 

		closedir HDIR;
	}
	else
	{
		NotifyMessage("BuildSubDirList - Failed to open directory: $PARENTDIR\n",16);
	}

	return @DIRLIST;
}

sub TrimSubDirs($@$)
{
	my ($PARENTDIR) = $_[0];
	my (@DIRLIST) = @{$_[1]};
	my ($VERSIONSTOKEEP) = $_[2];
	my $LISTSIZE = @DIRLIST;
	my $REMOVEPATH="";
	my $REMOVEDIR="";
	my $FILESREMOVED=0;

	#
	# Remove any backup or log directories older than specified versions to keep
	#    
	if ( $DEBUG )
	{
		DebugLog("Trimming directory: $PARENTDIR...\n");
		DebugLog("Number of subdirectories to keep: $VERSIONSTOKEEP\n");
		DebugLog("Number of subdirectories present: $LISTSIZE\n");
	}

	if ( $VERSIONSTOKEEP < $LISTSIZE )
	{
		#
		# Sort directories to remove the oldest
		# 
		@DIRLIST = sort ( @DIRLIST );
		@DIRLIST = reverse ( @DIRLIST );

		for ( my $i = $VERSIONSTOKEEP; $i < $LISTSIZE; ++$i )
		{
			if ( -d "$PARENTDIR/$DIRLIST[$i]" )
			{
				$REMOVEPATH = "$PARENTDIR/$DIRLIST[$i]";

				if ( $DEBUG ) { DebugLog("Removing subdirectory: $REMOVEPATH\n"); }

				File::Path::rmtree( $REMOVEPATH );   
			}	
		}
	}   

	if ( $DEBUG )
	{
		DebugLog("Completed subdirectory trim.\n");
	}
}

#
# MAIN
#

#
# If the mapping options include the job date/timestamp then 
# prune the target directory so we don't fill up the storage.
#
if (lc($PathMappingOptions) =~ m/job date/)
{
	my $TargetPath = $TargetDir;
	my @SubDirList;

	if (lc($PathMappingOptions) =~ m/source host/)
	{
		$TargetPath = "$TargetPath/$SourceAgent";
	}

	#
	# Attempt a simple deletion of target dir which will only succeed if it is empty.
	# This is so we don't build up large numbers of empty date/time dirs.
	#
	rmdir("$TargetPath/$JobDateTimeStamp");

	if ($MaximumJobDateTimeDirectories =~ m/^(\d+)$/ ) # Positive numeric value
	{
		@SubDirList = BuildSubDirList($TargetPath);
		TrimSubDirs($TargetPath,\@SubDirList,$MaximumJobDateTimeDirectories);
	}
}

exit 0;

