#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionModifyInputValue%
%script_lib_obj:SolutionSplitPaths%
%script_lib_obj:SolutionSigListXML%
%script_lib_obj:SolutionValidateUnixAbsolutePath%
%script_lib_obj:SolutionValidateWindowsAbsolutePath%
%script_lib_obj:SolutionDebugLog%
%script_lib_obj:SolutionGetBaseDDSInstallDir%

require File::Path;

my $ManifestInputMode=$FALSE;
my $ManifestInputFile;
my $EnableFileListOutputs;
my $SourceDeletionsPostTransfer;

my $SourceAgent;
my $ErrorInJob;
my $RET;
my $ddsFileUtilExists = 0;

my $ddsFileUtilCommand = "dds_fileutil";
my $isWin = $^O=~/Win/?1:0;    
if($^O=~/Win/){
	$ddsFileUtilCommand = "dds_fileutil.exe";
}

#
# Setup Database environment for making agent-related queries and updates.
#
my $baseDir = GetBaseDDSInstallDirectory();

#
# Function: Initialize
#
# Description: Initialize all global variables.
#
sub Initialize()
{
	$ManifestInputMode=q|%ManifestInputMode%|;
	$ManifestInputFile=q|%ManifestInputFile% |;

	$SourceDeletionsPostTransfer = q|%SourceDeletionsPostTransfer%|;

	$EnableFileListOutputs=q|%EnableFileListOutputs%|;

	$SourceAgent = q|%src_agent%|;
	$ErrorInJob = q|%ErrorInJob%|;
	if(-f "$baseDir/bin/dds_fileutil.exe" || -f "$baseDir/bin/dds_fileutil"){
	   $ddsFileUtilExists = 1;
	}
}

sub getInfoForFileDir($)
{
    my ($file) = @_;
     my $fuOptsFile = `mktmpfile`;
    chomp $fuOptsFile;
    if (!open(FUOPTS, '>', $fuOptsFile)) {
       print STDERR "WARNING: Source directory getinfo skipped -- could not open dds_finduiles option file '$fuOptsFile' ($!)\n";
       unlink $fuOptsFile;
       return;
    }
    print FUOPTS "\"$file\"\n";
    close FUOPTS;
    my @output= `"$baseDir/bin/$ddsFileUtilCommand" getinfo -A "$fuOptsFile"`;
    unlink $fuOptsFile;
    my $retString;
    foreach (@output)
    {
       chomp($_);
       $retString = $_;
    }
    my %hashStaus = split /[' '=]/, $retString;
    return %hashStaus;
}

sub fileExist($)
{
    my ($file) = @_;

    if($ddsFileUtilExists){
        my %hashStaus=getInfoForFileDir($file);
        if($hashStaus{'status'} eq 'file'){
            return 1;
        }else{
            return 0;
        }
    }else{
        if(-f $file){
            return 1;
        }else{
            return 0;
        }
    }
}

sub dirExist($)
{
    my ($dir) = @_;

    if($ddsFileUtilExists){
        my %hashStaus=getInfoForFileDir($dir);
        if($hashStaus{'status'} eq 'directory'){
            return 1;
        }else{
            return 0;
        }
    }else{
        if(-d $dir){
            return 1;
        }else{
            return 0;
        }
    }
}

sub deleteFileDir($)
{
    my ($file) = @_;
    if($ddsFileUtilExists){
        my %hashStaus=getInfoForFileDir($file);
        if($hashStaus{'status'} eq 'notfound' || $hashStaus{'status'} eq 'error' || $hashStaus{'status'} eq 'other'){
           if(-f $file){
    	      my $UNLINK = unlink($file);
              if ($UNLINK == 0){
	          return 0;
   	      }else{
	          return 1;
	      }
    	   }else{
              if(rmdir($file)){
                  return 1;
              }else{
                  return 0;
              }
           }
        }else{
           my $return = 1;
           my $fuOptsFile = `mktmpfile`;
           chomp $fuOptsFile;
           if (!open(FUOPTS, '>', $fuOptsFile)) {
              print STDERR "WARNING: Source directory remove skipped -- could not open dds_finduiles option file '$fuOptsFile' ($!)\n";
              unlink $fuOptsFile;
              return;
           }
           print FUOPTS "-f\n";
           print FUOPTS "\"$file\"\n";
           close FUOPTS;
           $return = system("\"$baseDir/bin/$ddsFileUtilCommand\" remove -A \"$fuOptsFile\"");
           unlink $fuOptsFile;
           print STDOUT "$ddsFileUtilCommand remove -f $file return: $return\n" if $DEBUG;
    	   if($return){
    	       if($hashStaus{'status'} eq 'file'){
    	   	  my $UNLINK = unlink($file);
    	          if ($UNLINK == 0){
	             return 0;
                   }else{
	             return 1;
	          }
    	       }if($hashStaus{'status'} eq 'directory '){
    	          if(rmdir($file)){
                      return 1;
                  }else{
                      return 0;
                  }
    	       }
    	   }
    	   return 1;    	   
        }
    }else{
    	if(-f $file){
    	   my $UNLINK = unlink($file);
    	   if ($UNLINK == 0){
	       return 0;
	   }else{
	       return 1;
	   }
    	}else{
           if(rmdir($file)){
               return 1;
           }else{
               return 0;
           }
        }
    }
}

sub checkIfFileIsOffline($)
{
    my ($file) = @_;
    my $isWin = $^O=~/Win/?1:0;    
    # Do not delete offline files on windows
    my $isOfflineFile = "";
    if($^O=~/Win/){
      if($ddsFileUtilExists){
        my %hashStaus=getInfoForFileDir($file);
        if($hashStaus{'status'} eq 'notfound' || $hashStaus{'status'} eq 'error' || $hashStaus{'status'} eq 'other'){
           my @output = `powershell.exe -inputformat none (Get-ItemProperty -Path '$file').attributes`;
           foreach (@output)
           {
              chomp($_);
              $isOfflineFile = $_;
           }

           if($isOfflineFile =~ m/Offline/){
              return 1;
           }else{
              return 0;
           }
        }else{
           if($hashStaus{'attrs'} eq 'o'){
              return 1;
           }else{
              return 0;
           }   
        }
      }else{
         my @output = `powershell.exe -inputformat none (Get-ItemProperty -Path '$file').attributes`;
         foreach (@output)
         {
            chomp($_);
            $isOfflineFile = $_;
         }

         if($isOfflineFile =~ m/Offline/){
            return 1;
         }else{
            return 0;
         }
      }
    }
    return 0;
}

sub deassertDirectoryDeletionBecauseRecentlyUpdated($)
{
    my ($file) = @_;
    use constant GRACE_PERIOD_SECONDS => 300;	## don't delete directories updated within the last 5 minutes
    use constant STAT_MTIME_INDEX => 9;			## index of modification time in 'stat' list

    if($ddsFileUtilExists){
       my %hashStaus=getInfoForFileDir($file);
       if($hashStaus{'status'} eq 'notfound' || $hashStaus{'status'} eq 'error' || $hashStaus{'status'} eq 'other'){
          if (((stat($file))[STAT_MTIME_INDEX] + GRACE_PERIOD_SECONDS) > time()) {
             return 1;	## de-assert directory deletion because it's been recently updated
          }else{
             return 0;
          }
       }else{
          if (($hashStaus{'mtime'} + GRACE_PERIOD_SECONDS) > time()) {
            return 1;	## de-assert directory deletion because it's been recently updated
          }else{
             return 0;
          }			
       }
    }else{
       if (((stat($file))[STAT_MTIME_INDEX] + GRACE_PERIOD_SECONDS) > time()) {
          return 1;	## de-assert directory deletion because it's been recently updated
       }else{
          return 0;
       }
    }
}

sub RemoveSourceFiles()
{
	print STDOUT "Removing source files from host '$SourceAgent' ...\n";

	#
	# First, put together hash list of any files that were marked as INUSE during transfer operations.
	#
	my %InuseList = ();

	my $manifestSuffix = "%manifestSuffix%";
	my @listOfInuseManifests = <inuse_*_$manifestSuffix>;
	foreach my $inuseMnfstFile (@listOfInuseManifests)
	{
		my $mnfstFH;
		if (!open($mnfstFH, "<$inuseMnfstFile"))
		{
			printf STDERR "ERROR: Unable to open INUSE manifest file '$inuseMnfstFile' for reading\n";
			next;
		}

		print STDOUT "Processing INUSE manifest file '$inuseMnfstFile'\n" if $DEBUG;

		my @mnfstContent = <$mnfstFH>;
		close $mnfstFH;

		foreach my $inuseFile (@mnfstContent)
		{
			chomp $inuseFile;
			$InuseList{$inuseFile} = 1;
			print STDOUT "Adding file '$inuseFile' to internal INUSE list\n" if $DEBUG;
		}
	}

	#
	# Now, attempt deletion of source files in transfer manifest
	#
	my $TransferManifest = q|%TransferManifest%|;

	my $NumFilesRemoved = 0;

	if (! -f $TransferManifest )
	{
		SigPrintLog("ERROR: Transfer manifest file not found: $TransferManifest",LOGERROR);
		return;
	}

	if (!open(TRANSFERMANIFEST,$TransferManifest))
	{
		SigPrintLog("ERROR: Transfer manifest file could not be opened: $TransferManifest",LOGERROR);
		return;
	}

	while (!eof(TRANSFERMANIFEST))
	{
		my $FileName = <TRANSFERMANIFEST>;

		chomp($FileName);

		if (fileExist($FileName))
		{
			print STDOUT "Checking for file '$FileName ' in internal INUSE list\n" if $DEBUG;

		    if (defined($InuseList{$FileName}))
			{
                           print STDOUT "Bypassing removal of INUSE file '$FileName' from host '$SourceAgent'\n" if $DEBUG;
                           next;
			}

			# Do not delete offline files on windows
			if(checkIfFileIsOffline($FileName)){
				print STDERR "File $FileName is offline and will not be deleted from source.\n";
			}else{
				my $UNLINK = deleteFileDir($FileName);

				if ($UNLINK == 0)
				{
					print STDERR "WARNING: Unable to remove file '$FileName' from host '$SourceAgent'\n";
				}
				else
				{
					print STDOUT "Removed file '$FileName' from host '$SourceAgent'\n" if $DEBUG;
					$NumFilesRemoved++;
				}
			}
		}
	}

	close(TRANSFERMANIFEST);
}

sub RemoveEmptyDirectories()
{
	print STDOUT "Attempting to remove empty source directories from host '$SourceAgent' ...\n";

	if ($ManifestInputMode) {
		print STDERR "WARNING: Source directory removal skipped -- unsupported \"Manifest Input Mode\" setting.\n";
		return;
	}

	my $topLevelDirOpts = "";
	my $excludedDirPaths = "";
	my $excludedDirs = "";
	my $ffOptsFile = "";
	my %isTopLevelDir = ();

	#
	# Process 'SourceData' item to construct 'findfiles' options string for top-level directories (i.e. '-path <dir>').
	#

	my $SourceData = q|%SourceData% |;
	my @sourcePathArray;

	if (!IsSigListXmlFormat($SourceData)) {
		@sourcePathArray = SolutionSplitPaths($SourceData, ',');	# SourceData is in legacy format
	} else {
		my @elementArray;
		my $xmlPathListType;
		my $xmlXattrs;

		if (SigListXMLParse($SourceData, \$xmlPathListType, \@elementArray, \$xmlXattrs) != 0) {
			print STDERR "WARNING: Source directory removal skipped -- SourceData XML specification is not parsable\n";
			return;
		}

		if (uc($xmlPathListType) ne "FILEDIR" && uc($xmlPathListType) ne "PATHLIST") {
			print STDERR "WARNING: Source directory removal skipped -- encountered unsupported pathlist type '$xmlPathListType'.\n";
			return;
		}

		if (uc($xmlPathListType) eq "FILEDIR") {
			@sourcePathArray = SigListGetELementsByAttribute(\@elementArray, "V");
		} else { ## PATHLIST
			@sourcePathArray = SolutionSplitPaths($xmlXattrs, ',');
		}
	}

	for (my $i = 0; $i < scalar(@sourcePathArray); $i++) {
		$sourcePathArray[$i] = ModifyInputValue($sourcePathArray[$i], 0);
		if (! -d $sourcePathArray[$i]) {
			print STDERR "WARNING: Non-directory path '$sourcePathArray[$i]' skipped during source directory removal.\n";
			next;
		}

		$topLevelDirOpts .= "-path \"$sourcePathArray[$i]\"\n";
	}

	if (! $topLevelDirOpts) {
		print STDERR "WARNING: Source directory removal skipped -- no top-level directories are available for traversal.\n";
		return;
	}

	#
	# Create/format 'findfiles' options file to generate a "normalized" list of top-level directories, then execute the
	# 'findfiles' command to populate the '%isTopLevelDir' hash.
	#

	$ffOptsFile = `mktmpfile`;
	chomp $ffOptsFile;

	if (!open(FFOPTS, '>', $ffOptsFile)) {
		print STDERR "WARNING: Source directory removal skipped -- could not open findfiles option file '$ffOptsFile' ($!)\n";
		unlink $ffOptsFile;
		return;
	}

	print FFOPTS "$topLevelDirOpts\n";
	print FFOPTS "-depth 0\n";

	close FFOPTS;

	if (!open(FFOUT, "findfiles -- -optfile \"$ffOptsFile\" |")) {
		print STDERR "WARNING: Source directory removal skipped -- could not execute top-level directories 'findfiles' cmd ($!)\n";
		unlink $ffOptsFile;
		return;
	}

	while (my $topLevelDir = <FFOUT>) {
		chomp $topLevelDir;
		$isTopLevelDir{$topLevelDir} = 1;
	}

	close FFOUT;

	#
	# Process 'SourceDeletionExcludeDirectories' item to construct 'findfiles' options string for directory traversal exclusions
	# (i.e. '-pathexclude <dir>' and '-direxclude <dirpattern>').
	#

	my $SourceDeletionExcludeDirectories = q|%SourceDeletionExcludeDirectories% |;

	if ($SourceDeletionExcludeDirectories =~ s/^\s*(.*?)\s*$/$1/) {
		for my $exclDir (split /\s*,\s*/, $SourceDeletionExcludeDirectories) {
			$exclDir = ModifyInputValue($exclDir, 0);
			if (   ($IsWindows && SolutionValidateWindowsAbsolutePath($exclDir, 0))
				|| ($IsUnix && SolutionValidateUnixAbsolutePath($exclDir, 0)))
			{
				$excludedDirPaths .= ($excludedDirPaths ? "," : "") . $exclDir;
			}
			else
			{
				$excludedDirs .= ($excludedDirs ? "," : "") . "@" . $exclDir;
			}
		}
	}

	#
	# Re-format 'findfiles' options file to generate a "bottom-up" ordered list of directories below the specified
	# top-level directories and with the specified directory exclusions.
	#

	if (!open(FFOPTS, '>', $ffOptsFile)) {
		print STDERR "WARNING: Source directory removal skipped -- could not re-open findfiles option file '$ffOptsFile' ($!)\n";
		unlink $ffOptsFile;
		return;
	}

	print FFOPTS "$topLevelDirOpts\n";
	print FFOPTS "-exclude \"*\"\n";
	print FFOPTS "-reverse\n";

	if ($excludedDirPaths) {
		print FFOPTS "-pathexclude \"$excludedDirPaths\"\n";
	}

	if ($excludedDirs) {
		print FFOPTS "-direxclude \"$excludedDirs\"\n";
	}

	close FFOPTS;

	#
	# Execute 'findfiles' directory listing command and process the output lines to identify empty directories that
	# haven't been recently modified (i.e. within the designated grace period). Such processing for top-level directories
	# is skipped because we don't want to delete those.
	#
	# When all output lines have been processed, delete all directories marked for deletion.
	#

	my @FFOUT= `"$baseDir/bin/findfiles" -- -optfile "$ffOptsFile" `;

	my $dirPathSep = ($IsUnix ? '/' : '\\');

	my %directoryMarkedForDeletion;

	foreach (@FFOUT){
		chomp($_);
		my $dirName = $_;
		next if ($isTopLevelDir{$dirName});

		# Read entries for current directory (omitting '.' and '..'). On Windows, the directory scan has the
		# added benefit of causing the modification time to be updated for retrieval by 'stat'.
		#
		my @dirEntries = ();
		my $markDirForDeletion = 1;	## assume deletion until we know better

		if($ddsFileUtilExists){
		   my %hashStaus=getInfoForFileDir($dirName);
		   if($hashStaus{'status'} eq 'notfound' || $hashStaus{'status'} eq 'error' || $hashStaus{'status'} eq 'other'){
				if (!opendir(DIRHANDLE, $dirName)) {
					print STDERR "WARNING: Unable to scan directory '$dirName' during source directory removal ($!)\n";
					$markDirForDeletion = 0;
				} else {
					@dirEntries = grep !/^\.\.?$/, readdir DIRHANDLE;
					closedir DIRHANDLE;
				}
		   }else{
				my $fuOptsFile = `mktmpfile`;
    				chomp $fuOptsFile;
    				if (!open(FUOPTS, '>', $fuOptsFile)) {
       				   print STDERR "WARNING: Source directory listdir skipped -- could not open dds_finduiles option file '$fuOptsFile' ($!)\n";
       				   unlink $fuOptsFile;
       				   return;
    				}
				print FUOPTS "\"$dirName\"\n";
    				close FUOPTS;
    				my @FFOUT= `"$baseDir/bin/$ddsFileUtilCommand" listdir -A "$fuOptsFile"`;
	    			unlink $fuOptsFile;

				foreach (@FFOUT){
					chomp($_);
					my $subDirName = $_;
					push(@dirEntries,$subDirName);
				}

				my $retError = system("\"$baseDir/bin/$ddsFileUtilCommand\" listdir  \"$dirName\" ");
				if($retError){
					print STDERR "WARNING: Unable to scan directory '$dirName' during source directory removal ($!)\n";
					$markDirForDeletion = 0;
				}		
		   }
		}else{
		   if (!opendir(DIRHANDLE, $dirName)) {
				print STDERR "WARNING: Unable to scan directory '$dirName' during source directory removal ($!)\n";
				$markDirForDeletion = 0;
			} else {
				@dirEntries = grep !/^\.\.?$/, readdir DIRHANDLE;
				closedir DIRHANDLE;
			}
		}

		if (scalar(@dirEntries) > 0) {
			for my $subDir (@dirEntries) {
				if ($directoryMarkedForDeletion{"$dirName$dirPathSep$subDir"} == 0) {
					$markDirForDeletion = 0;	## de-assert directory deletion because a subdirectory is not being deleted
					last;
				}
			}
		}

		if (deassertDirectoryDeletionBecauseRecentlyUpdated($dirName)) {
			$markDirForDeletion = 0;	## de-assert directory deletion because it's been recently updated
		}

		$directoryMarkedForDeletion{$dirName} = $markDirForDeletion;
		if ($markDirForDeletion){
			print STDOUT "Marking folder '$dirName' for deletion\n" if $DEBUG;
		}
	}

	for my $dirName (reverse sort keys %directoryMarkedForDeletion) {
		if ($directoryMarkedForDeletion{$dirName} || !($SourceDeletionsPostTransfer =~ /emptydirs/i)) {
			print STDOUT "Deleting folder '$dirName'\n" if $DEBUG;
			# Do not delete offline files on windows
			if(checkIfFileIsOffline($dirName)){
				print STDERR "Folder $dirName is offline and will not be deleted from source.\n";
			}else{
				if (!deleteFileDir($dirName)) {
					print STDERR "WARNING: Unable to remove directory '$dirName' during source directory removal ($!)\n";
				}
				else
				{
					print STDOUT "Success\n" if $DEBUG;;
				}
			}
		}
	}

	unlink $ffOptsFile;
}

###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

#
# If all has gone well then optionally perform deletion of transferred files and/or empty directories.
#
if ($SourceDeletionsPostTransfer ne "" && lc($SourceDeletionsPostTransfer) ne "none")
{
	if ($ErrorInJob != 0 || %exit_code% != 0)
	{
		print STDERR "WARNING: Source file/directory removal skipped due to transfer errors.\n";
	}
	else
	{
		if ($SourceDeletionsPostTransfer =~ /xfrdfiles/i)
		{
			RemoveSourceFiles();
		}

		if ($SourceDeletionsPostTransfer =~ /emptydirs/i)
		{
			RemoveEmptyDirectories();

		} elsif ($SourceDeletionsPostTransfer =~ /dirs/i){
			RemoveEmptyDirectories();
		}
	}
}

#
# For manifest-based transfers, remove the transfer manifest from the source
#
if ($ManifestInputMode)
{
	unlink($ManifestInputFile);
}

my $manifestSuffix = "%manifestSuffix%";
my @listOfInuseManifests = <inuse_*_$manifestSuffix>;
foreach my $inuseMnfstFile (@listOfInuseManifests)
{
    	unlink $inuseMnfstFile;
}

if (!$EnableFileListOutputs)
{
	print STDOUT "File list outputs disabled due to multiple target agents specified.\n";
}

#
# Set output properties...
#
my $TransferSummaryStats = q|%package_summary_stats% | ;
my $TransferDetailStats = q|%package_report_stats% | ;

print STDOUT "%dds_property_set%Outputs.TransferSummaryStats=$TransferSummaryStats\n";
print STDOUT "%dds_property_set%Outputs.TransferDetailStats=$TransferDetailStats\n";

if (($ErrorInJob == 0) && (%exit_code% == 0))
{
	my $ReturnCode = 0;
	my $ReturnMessage = "SigniantObjectUpload Component Completed Successfully\n";
	print STDOUT "%dds_property_set%Outputs.ReturnCode=$ReturnCode\n";
	print STDOUT "%dds_property_set%Outputs.ReturnMessage=$ReturnMessage\n";
}
else
{
	my $ReturnCode = 1;
	my $ReturnMessage = "SigniantObjectUpload Component Completed with Errors\n";
	print STDOUT "%dds_property_set%Outputs.ReturnCode=$ReturnCode\n";
	print STDOUT "%dds_property_set%Outputs.ReturnMessage=$ReturnMessage\n";
}

exit(0);

