#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%
%script_lib_obj:SolutionSigListXML%

my $TransferredManifest="";
my $TransferredManifestCreated=0;
my $SkippedManifest="";
my $SkippedManifestCreated=0;

my $EnableFileListOutputs="";
my $TransferredXMLString="";
my $SkippedXMLString="";
my $AggregateXMLString="";
my $AggregateTopLevelContent="";

my $RET=0;

sub Initialize()
{
	my $DdsTargetTempDir = q|%DdsTargetTempDir%|;
	my $manifestSuffix = q|%manifestSuffix%|;

	$TransferredManifest = $DdsTargetTempDir . '/transferred_files.' . $manifestSuffix;

	$SkippedManifest = $DdsTargetTempDir . '/skipped_files.' . $manifestSuffix;

	$EnableFileListOutputs= q|%EnableFileListOutputs%|;
}

sub BuildTransferredFileList()
{
	#
	# Read the manifest files into arrays...
	#
	my @TransferredManifestFiles = ();
	my @SkippedManifestFiles = ();

	if (!open(TRANSFERREDMANIFEST, "$TransferredManifest"))
	{
		SigPrintLog("ERROR: Could not open transferred files manifest: $TransferredManifest", LOGERROR, LOGSTDOUT);
		$RET=1;
	}
	else
	{
		@TransferredManifestFiles = <TRANSFERREDMANIFEST>;
		close TRANSFERREDMANIFEST;
	}

	unlink($TransferredManifest);

	if (!open(SKIPPEDMANIFEST, "$SkippedManifest"))
	{
		SigPrintLog("ERROR: Could not open skipped files manifest: $SkippedManifest", LOGERROR, LOGSTDOUT);
		$RET=1;
		return;
	}
	else
	{
		@SkippedManifestFiles = <SKIPPEDMANIFEST>;
		close SKIPPEDMANIFEST;
	}

	unlink($SkippedManifest);

	$TransferredXMLString = "<SIGLIST TYPE=\'PATHLIST\' >";
	$SkippedXMLString = "<SIGLIST TYPE=\'PATHLIST\' >";
	$AggregateXMLString = "<SIGLIST TYPE=\'PATHLIST\' >";

	for (my $i=0; $i < scalar @TransferredManifestFiles; $i++)
	{
		$TransferredManifestFiles[$i] =~ s/[\n\r]//g;
		$TransferredXMLString = $TransferredXMLString . $TransferredManifestFiles[$i];
		$AggregateXMLString = $AggregateXMLString . $TransferredManifestFiles[$i];
	}

	for (my $i=0; $i < scalar @SkippedManifestFiles; $i++)
	{
		$SkippedManifestFiles[$i] =~ s/[\n\r]//g;
		$SkippedXMLString = $SkippedXMLString . $SkippedManifestFiles[$i];
		$AggregateXMLString = $AggregateXMLString . $SkippedManifestFiles[$i];
	}

	$TransferredXMLString = $TransferredXMLString . " </SIGLIST>";
	$SkippedXMLString = $SkippedXMLString . " </SIGLIST>";
	$AggregateXMLString = $AggregateXMLString . " </SIGLIST>";

	SigListXMLCollapseFileList($AggregateTopLevelContent,$AggregateXMLString);
}

sub SetOutputs()
{
	SigPrintLog("Outputs.TransferredFileList: [$TransferredXMLString]",LOGDEBUG);
	SigPrintLog("Outputs.skippedFileList: [$SkippedXMLString]",LOGDEBUG);
	SigPrintLog("Outputs.aggregateFileList: [$AggregateXMLString]",LOGDEBUG);
	SigPrintLog("Outputs.aggregateTopLevelContent: [$AggregateTopLevelContent]",LOGDEBUG);

	SigSetOutput("Outputs.TransferredFileList",$TransferredXMLString);
	SigSetOutput("Outputs.skippedFileList",$SkippedXMLString);
	SigSetOutput("Outputs.aggregateFileList",$AggregateXMLString);
	SigSetOutput("Outputs.aggregateTopLevelContent",$AggregateTopLevelContent);
}

###################################################################################################
############################################## MAIN ###############################################
###################################################################################################

Initialize();

if ($EnableFileListOutputs)
{
	BuildTransferredFileList();

	SetOutputs();
}

exit $RET;

#
# Copyright 2000-2011 Signiant Inc.
#

#!dds_perl

%script_lib_obj:SolutionStandardHeaderPerl%

#
# Set output properties...
#

my $ReturnCode = 1;
my $ReturnMessage = "Signiant Advanced FileTransfer Component Completed with Errors\n";

print STDOUT "%dds_property_set%Outputs.ReturnCode=$ReturnCode\n";
print STDOUT "%dds_property_set%Outputs.ReturnMessage=$ReturnMessage\n";

exit(0);

#!dds_perl
select STDERR; $| = 1;
select STDOUT; $| = 1;
if(1){
print STDOUT "%dds_set%GROUP_COMMAND_RESULT=0\n";
exit(0);
}
exit(0);
